import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const DeliveryOrderPage = () => {
  const { orderId } = useParams();
  const { currentUser } = useAuth();
  const navigate = useNavigate();

  const [order, setOrder] = useState(null);
  const [pickupCode, setPickupCode] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [notification, setNotification] = useState(null);
  const [currentStatus, setCurrentStatus] = useState('');
  const [isConfirmModalOpen, setIsConfirmModalOpen] = useState(false);
  const [isPickupModalOpen, setIsPickupModalOpen] = useState(false);
  const wsRef = useRef(null);
  const retryQueueRef = useRef([]);

  // Initialize WebSocket connection for real-time updates
  useEffect(() => {
    if (!currentUser || !currentUser.token || !orderId) return;

    // Close any existing connection
    if (wsRef.current) {
      wsRef.current.close();
    }

    // Create new WebSocket connection
    const wsUrl = `${process.env.REACT_APP_WS_URL || 'wss://api.yourbackend.com'}/orders`;
    wsRef.current = new WebSocket(wsUrl);

    wsRef.current.onopen = () => {
      console.log('WebSocket connection established');
      // Identify as a delivery person and subscribe to this specific order
      wsRef.current.send(JSON.stringify({
        type: 'IDENTIFY',
        role: 'DELIVERY',
        deliveryPersonId: currentUser.id,
        orderId
      }));

      // Retry any failed operations when connection is available
      if (retryQueueRef.current.length > 0) {
        retryFailedOperations();
      }
    };

    wsRef.current.onmessage = (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'ORDER_UPDATE' && data.orderId === parseInt(orderId)) {
        // Handle order status update
        showNotification(`Order status updated to ${data.status}`);
        setCurrentStatus(data.status);
        fetchOrderDetails();
      } else if (data.type === 'PICKUP_VERIFIED' && data.orderId === parseInt(orderId)) {
        // Handle pickup verification by restaurant
        showNotification('Restaurant verified pickup! You can now deliver the order.');
        setCurrentStatus('PICKED_UP');
        fetchOrderDetails();
      }
    };

    wsRef.current.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    wsRef.current.onclose = () => {
      console.log('WebSocket connection closed');
    };

    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, [currentUser, orderId]);

  // Function to show notification
  const showNotification = (message) => {
    setNotification(message);
    // Clear notification after 5 seconds
    setTimeout(() => {
      setNotification(null);
    }, 5000);
  };

  // Store failed operations for retry
  const storeFailedOperation = (type, data) => {
    const newOperation = {
      id: Date.now(),
      type,
      data,
      timestamp: new Date()
    };

    retryQueueRef.current = [...retryQueueRef.current, newOperation];

    // Store in localStorage for persistence
    try {
      localStorage.setItem('deliveryOrderFailedOperations', JSON.stringify(retryQueueRef.current));
    } catch (err) {
      console.error('Error storing failed operation:', err);
    }
  };

  // Remove operation from retry queue
  const removeFailedOperation = (operationId) => {
    retryQueueRef.current = retryQueueRef.current.filter(op => op.id !== operationId);

    // Update localStorage
    try {
      localStorage.setItem('deliveryOrderFailedOperations', JSON.stringify(retryQueueRef.current));
    } catch (err) {
      console.error('Error updating failed operations in storage:', err);
    }
  };

  // Retry failed operations
  const retryFailedOperations = async () => {
    const operations = [...retryQueueRef.current];

    for (const op of operations) {
      try {
        if (op.type === 'updateOrderStatus') {
          await updateOrderStatusWithRetry(op.data.status, 1);
          removeFailedOperation(op.id);
        }
        // Add other operation types here
      } catch (err) {
        console.error('Failed to retry operation:', err);
        // Keep in queue for later retry
      }
    }
  };

  // Load any failed operations from localStorage on component mount
  useEffect(() => {
    try {
      const storedOperations = localStorage.getItem('deliveryOrderFailedOperations');
      if (storedOperations) {
        retryQueueRef.current = JSON.parse(storedOperations);
      }
    } catch (err) {
      console.error('Error loading failed operations:', err);
    }
  }, []);

  // Fetch order details and pickup code
  const fetchOrderDetails = async () => {
    try {
      setLoading(true);

      // Fetch order details
      const orderResponse = await axios.get(`/api/delivery/orders/${orderId}`, {
        headers: {
          Authorization: `Bearer ${currentUser.token}`
        }
      });

      setOrder(orderResponse.data);
      setCurrentStatus(orderResponse.data.status);

      // Fetch pickup code if order is in ASSIGNED_TO_DELIVERY status
      if (orderResponse.data.status === 'ASSIGNED_TO_DELIVERY' || 
          orderResponse.data.status === 'READY_FOR_PICKUP') {
        const codeResponse = await axios.get(`/api/delivery/orders/${orderId}/pickup-code`, {
          headers: {
            Authorization: `Bearer ${currentUser.token}`
          }
        });

        if (codeResponse.data.pickupCode) {
          setPickupCode(codeResponse.data.pickupCode);
        }
      }

      setLoading(false);
    } catch (err) {
      setError('Failed to fetch order details. Please try again later.');
      setLoading(false);
      console.error('Error fetching order details:', err);
    }
  };

  useEffect(() => {
    if (orderId && currentUser) {
      fetchOrderDetails();
    }
  }, [orderId, currentUser]);

  // Enhanced update order status with retry logic
  const updateOrderStatusWithRetry = async (newStatus, maxRetries = 3) => {
    let retries = 0;

    while (retries < maxRetries) {
      try {
        console.log(`Updating order ${orderId} to status ${newStatus} (attempt ${retries + 1}/${maxRetries})`);

        const response = await axios.put(`/api/delivery/orders/${orderId}/status`, 
          { status: newStatus },
          {
            headers: {
              Authorization: `Bearer ${currentUser.token}`
            }
          }
        );

        // After successful update, update local state
        setCurrentStatus(newStatus);

        // Send update via WebSocket
        if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
          wsRef.current.send(JSON.stringify({
            type: 'STATUS_UPDATED',
            orderId: parseInt(orderId),
            status: newStatus,
            deliveryPersonId: currentUser.id
          }));
        }

        showNotification(`Order status updated to ${newStatus}`);

        // If order is completed, redirect to available orders after a delay
        if (newStatus === 'DELIVERED') {
          setTimeout(() => {
            navigate('/delivery/available-orders');
          }, 3000);
        }

        return response;
      } catch (err) {
        retries++;
        console.error(`Error updating order status (attempt ${retries}/${maxRetries}):`, err);

        if (retries >= maxRetries) {
          // After all retries, store failed operation for later retry
          storeFailedOperation('updateOrderStatus', { status: newStatus });
          setError(`Failed to update order status. It will be retried automatically when connection is restored.`);
          throw err;
        }

        // Wait before retrying (exponential backoff)
        await new Promise(resolve => setTimeout(resolve, 1000 * Math.pow(2, retries)));
      }
    }
  };

  // Handle initiating pickup process
  const handleInitiatePickup = () => {
    setIsPickupModalOpen(true);
  };

  // Handle pickup confirmation
  const handlePickupConfirm = async () => {
    try {
      // First, attempt to arrive at restaurant (this will trigger the restaurant to verify the pickup)
      await axios.post(`/api/delivery/orders/${orderId}/arrive-at-restaurant`, {}, {
        headers: {
          Authorization: `Bearer ${currentUser.token}`
        }
      });

      showNotification(`You've arrived at the restaurant. Show them the pickup code: ${pickupCode}`);

      // Close the pickup modal
      setIsPickupModalOpen(false);

      // Note: The actual status update to PICKED_UP will happen after the restaurant verifies the pickup code
    } catch (err) {
      setError('Failed to process arrival at restaurant. Please try again.');
      console.error('Error processing arrival at restaurant:', err);
    }
  };

  // Handle delivery confirmation
  const handleDeliveryConfirm = () => {
    setIsConfirmModalOpen(true);
  };

  // Complete delivery
  const completeDelivery = async () => {
    try {
      await updateOrderStatusWithRetry('DELIVERED');
      setIsConfirmModalOpen(false);
    } catch (err) {
      setError('Failed to complete delivery. Please try again.');
    }
  };

  // Cancel confirmation
  const cancelConfirmation = () => {
    setIsConfirmModalOpen(false);
    setIsPickupModalOpen(false);
  };

  // Open Google Maps for navigation
  const openGoogleMaps = (address) => {
    // Format the address for Google Maps URL
    const formattedAddress = encodeURIComponent(
      `${address.street}, ${address.city}, ${address.zipCode}`
    );
    window.open(`https://www.google.com/maps/search/?api=1&query=${formattedAddress}`, '_blank');
  };

  // Call customer
  const callCustomer = (phoneNumber) => {
    window.location.href = `tel:${phoneNumber}`;
  };

  if (loading) return (
    <div className="text-center p-10">
      <div className="spinner-border" role="status">
        <span className="sr-only">Loading order details...</span>
      </div>
      <p className="mt-2">Loading order details...</p>
    </div>
  );

  if (error) return (
    <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 m-4" role="alert">
      <p>{error}</p>
      <button 
        onClick={() => window.location.reload()}
        className="mt-2 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
      >
        Try Again
      </button>
    </div>
  );

  if (!order) return <div className="text-center p-10">Order not found</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Notification Banner */}
      {notification && (
        <div className="bg-blue-100 border-l-4 border-blue-500 text-blue-700 p-4 mb-4 flex justify-between items-center">
          <p>{notification}</p>
          <button onClick={() => setNotification(null)} className="text-blue-700">✕</button>
        </div>
      )}

      <h1 className="text-2xl font-bold mb-6">Order #{orderId}</h1>

      {/* Pickup Code Alert (if available) */}
      {pickupCode && (currentStatus === 'ASSIGNED_TO_DELIVERY' || currentStatus === 'READY_FOR_PICKUP') && (
        <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4 mb-6">
          <h3 className="font-bold">Your Pickup Code: {pickupCode}</h3>
          <p>Show this code to the restaurant when you pick up the order.</p>
        </div>
      )}

      {/* Status Banner */}
      <div className={`mb-6 p-4 rounded-lg ${
        currentStatus === 'READY_FOR_PICKUP' || currentStatus === 'ASSIGNED_TO_DELIVERY' ? 'bg-blue-100' :
        currentStatus === 'PICKED_UP' ? 'bg-yellow-100' :
        currentStatus === 'DELIVERED' ? 'bg-green-100' :
        'bg-gray-100'
      }`}>
        <h2 className="font-semibold text-lg mb-2">Status: {currentStatus.replace(/_/g, ' ')}</h2>
        <div className="w-full bg-gray-200 rounded-full h-2.5">
          <div className={`h-2.5 rounded-full ${
            currentStatus === 'ASSIGNED_TO_DELIVERY' ? 'w-1/4 bg-blue-600' :
            currentStatus === 'READY_FOR_PICKUP' ? 'w-2/4 bg-blue-600' :
            currentStatus === 'PICKED_UP' ? 'w-3/4 bg-yellow-600' :
            currentStatus === 'DELIVERED' ? 'w-full bg-green-600' :
            'w-0'
          }`}></div>
        </div>
      </div>

      {/* Restaurant Info */}
      <div className="bg-white rounded-lg shadow-md p-6 border border-gray-200 mb-6">
        <h2 className="text-lg font-semibold mb-4">Restaurant</h2>
        <div className="mb-4">
          <p className="font-medium">{order.restaurant.name}</p>
          <p className="text-gray-500">{order.restaurant.address.street}</p>
          <p className="text-gray-500">{order.restaurant.address.city}, {order.restaurant.address.zipCode}</p>
          <p className="text-gray-500">{order.restaurant.phone}</p>
        </div>

        <button
          onClick={() => openGoogleMaps(order.restaurant.address)}
          className="flex items-center px-4 py-2 bg-gray-100 text-gray-800 rounded-lg hover:bg-gray-200"
        >
          Navigate to Restaurant
        </button>
      </div>

      {/* Customer Info */}
      <div className="bg-white rounded-lg shadow-md p-6 border border-gray-200 mb-6">
        <h2 className="text-lg font-semibold mb-4">Customer</h2>
        <div className="mb-4">
          <p className="font-medium">{order.customer.name}</p>
          <p className="text-gray-500">{order.deliveryAddress.street}</p>
          <p className="text-gray-500">{order.deliveryAddress.city}, {order.deliveryAddress.zipCode}</p>
        </div>

        <div className="flex flex-col sm:flex-row gap-2">
          <button
            onClick={() => callCustomer(order.customer.phone)}
            className="flex items-center px-4 py-2 bg-green-100 text-green-800 rounded-lg hover:bg-green-200"
          >
            Call Customer
          </button>

          <button
            onClick={() => openGoogleMaps(order.deliveryAddress)}
            className="flex items-center px-4 py-2 bg-gray-100 text-gray-800 rounded-lg hover:bg-gray-200"
          >
            Navigate to Customer
          </button>
        </div>
      </div>

      {/* Order Details */}
      <div className="bg-white rounded-lg shadow-md p-6 border border-gray-200 mb-6">
        <h2 className="text-lg font-semibold mb-4">Order Items</h2>
        <ul className="divide-y divide-gray-200">
          {order.items.map((item) => (
            <li key={item.id} className="py-3 flex justify-between">
              <div>
                <p>{item.quantity}x {item.menuItem.name}</p>
                {item.specialInstructions && (
                  <p className="text-sm text-gray-500">Note: {item.specialInstructions}</p>
                )}
              </div>
              <p className="font-medium">${(item.price * item.quantity).toFixed(2)}</p>
            </li>
          ))}
        </ul>

        <div className="mt-4 pt-4 border-t border-gray-200">
          <div className="flex justify-between mb-2">
            <span>Subtotal</span>
            <span>${order.subtotal.toFixed(2)}</span>
          </div>
          <div className="flex justify-between mb-2">
            <span>Delivery Fee</span>
            <span>${order.deliveryFee.toFixed(2)}</span>
          </div>
          <div className="flex justify-between font-bold">
            <span>Total</span>
            <span>${order.totalPrice.toFixed(2)}</span>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="bg-white rounded-lg shadow-md p-6 border border-gray-200">
        {(currentStatus === 'READY_FOR_PICKUP' || currentStatus === 'ASSIGNED_TO_DELIVERY') && (
          <button
            onClick={handleInitiatePickup}
            className="w-full px-4 py-3 bg-blue-500 text-white font-medium rounded-lg hover:bg-blue-600"
          >
            Arrived at Restaurant (Show Pickup Code)
          </button>
        )}

        {currentStatus === 'PICKED_UP' && (
          <button
            onClick={handleDeliveryConfirm}
            className="w-full px-4 py-3 bg-green-500 text-white font-medium rounded-lg hover:bg-green-600"
          >
            Confirm Delivery to Customer
          </button>
        )}

        {currentStatus === 'DELIVERED' && (
          <div className="text-center">
            <p className="text-green-600 font-medium mb-2">
              ✓ Delivery completed successfully!
            </p>
            <p className="text-gray-500">You will be redirected to available orders shortly...</p>
          </div>
        )}
      </div>

      {/* Pickup Modal */}
      {isPickupModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
            <h3 className="text-xl font-bold mb-4">Confirm Pickup</h3>
            <p className="mb-2">You're at the restaurant to pick up order #{orderId}.</p>
            <div className="bg-yellow-50 p-4 rounded-lg mb-4">
              <p className="font-bold">Show this pickup code to the restaurant:</p>
              <div className="text-3xl font-bold text-center my-2">{pickupCode}</div>
              <p className="text-sm text-gray-600">The restaurant will verify this code to confirm the pickup.</p>
            </div>
            <div className="flex justify-end space-x-4">
              <button
                onClick={cancelConfirmation}
                className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300"
              >
                Cancel
              </button>
              <button
                onClick={handlePickupConfirm}
                className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                I'm at the Restaurant
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Confirmation Modal */}
      {isConfirmModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
            <h3 className="text-xl font-bold mb-4">Confirm Delivery</h3>
            <p className="mb-6">Are you sure you want to mark this order as delivered?</p>
            <div className="flex justify-end space-x-4">
              <button
                onClick={cancelConfirmation}
                className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300"
              >
                Cancel
              </button>
              <button
                onClick={completeDelivery}
                className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
              >
                Confirm Delivery
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DeliveryOrderPage;
