// frontend/src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import { CartProvider } from './context/CartContext';

// Pages
import LandingPage from './pages/LandingPage';
import Login from './pages/Login';
import SignUp from './pages/SignUp';
import Dashboard from './pages/Dashboard';
import NotFound from './pages/NotFound';

// Components
import ProtectedRoute from './components/ProtectedRoute';
import Navbar from './components/Navbar';
import Footer from './components/Footer';

// Styles
import './App.css';

// Simple placeholder for restaurant dashboard
const RestaurantDashboardPlaceholder = () => (
  <div style={{ padding: '40px', textAlign: 'center' }}>
    <h1>Restaurant Dashboard</h1>
    <p>You've successfully logged in as a restaurant user!</p>
    <p>We're rebuilding the restaurant components to fix the issues.</p>
  </div>
);

function App() {
  return (
    <AuthProvider>
      <CartProvider>
        <Router>
          <div className="app">
            <Routes>
              {/* Public routes */}
              <Route path="/" element={<LandingPage />} />
              <Route path="/login" element={<Login />} />
              <Route path="/signup" element={<SignUp />} />

              {/* Protected Dashboard route for customers */}
              <Route
                path="/dashboard"
                element={
                  <ProtectedRoute requiredRole="CUSTOMER">
                    <Navbar />
                    <Dashboard />
                    <Footer />
                  </ProtectedRoute>
                }
              />

              {/* Restaurant Owner Route - Just a single route to test */}
              <Route
                path="/restaurant/dashboard"
                element={
                  <ProtectedRoute requiredRole="RESTAURANT_STAFF">
                    <Navbar />
                    <RestaurantDashboardPlaceholder />
                    <Footer />
                  </ProtectedRoute>
                }
              />

              {/* 404 route */}
              <Route path="*" element={<NotFound />} />
            </Routes>
          </div>
        </Router>
      </CartProvider>
    </AuthProvider>
  );
}

export default App;
