// src/pages/restaurant/OrderProcessing.jsx
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import restaurantApi from '../../api/restaurantApi';
import orderApi from '../../api/orderApi';
import '../../styles/restaurant/OrderProcessing.css';

// Order status flow for restaurants
const ORDER_STATUS_FLOW = {
  NEW: {
    nextStatus: 'PROCESSING',
    nextLabel: 'Accept Order',
    color: 'text-primary',
    description: 'New incoming order'
  },
  PROCESSING: {
    nextStatus: 'READY_FOR_PICKUP',
    nextLabel: 'Mark Ready',
    color: 'text-warning',
    description: 'Preparing order'
  },
  READY_FOR_PICKUP: {
    nextStatus: 'OUT_FOR_DELIVERY',
    nextLabel: 'Start Delivery',
    color: 'text-success',
    description: 'Order is prepared'
  },
  OUT_FOR_DELIVERY: {
    nextStatus: 'COMPLETED',
    nextLabel: 'Complete Order',
    color: 'text-info',
    description: 'Order is out for delivery'
  }
};

const OrderProcessing = () => {
  const location = useLocation();
  const { user } = useAuth();

  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('new');

  // Fetch user's restaurants
  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        const response = await restaurantApi.getMyRestaurants();

        if (response.success) {
          const restaurantList = response.data;
          setRestaurants(restaurantList);

          // Select first restaurant if available
          if (restaurantList.length > 0) {
            setSelectedRestaurant(restaurantList[0]);
            fetchRestaurantOrders(restaurantList[0].id);
          }
        } else {
          console.error('Failed to fetch restaurants:', response.error);
        }
      } catch (error) {
        console.error('Error fetching restaurants:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchRestaurants();
  }, []);

  // Fetch orders for a specific restaurant
  const fetchRestaurantOrders = async (restaurantId, status = null) => {
    try {
      setLoading(true);
      const response = await orderApi.getRestaurantOrders(restaurantId, status);

      if (response.success) {
        // Sort orders by creation date (newest first)
        const sortedOrders = response.data.sort((a, b) => 
          new Date(b.createdAt) - new Date(a.createdAt)
        );
        setOrders(sortedOrders);
      } else {
        console.error('Failed to fetch orders:', response.error);
        setOrders([]);
      }
    } catch (error) {
      console.error('Error fetching orders:', error);
      setOrders([]);
    } finally {
      setLoading(false);
    }
  };

  // Handle restaurant selection
  const handleRestaurantChange = (e) => {
    const restaurantId = parseInt(e.target.value);
    const restaurant = restaurants.find(r => r.id === restaurantId);
    setSelectedRestaurant(restaurant);
    fetchRestaurantOrders(restaurantId);
  };

  // Update order status
  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    try {
      const response = await orderApi.updateOrderStatus(orderId, newStatus);

      if (response.success) {
        // Update local orders state
        setOrders(prevOrders => 
          prevOrders.map(order => 
            order.id === orderId 
              ? { ...order, status: newStatus } 
              : order
          )
        );
      } else {
        console.error('Failed to update order status:', response.error);
      }
    } catch (error) {
      console.error('Error updating order status:', error);
    }
  };

  // Render order card
  const renderOrderCard = (order) => {
    const statusInfo = ORDER_STATUS_FLOW[order.status] || {
      nextStatus: null,
      nextLabel: 'No Action',
      color: 'text-secondary',
      description: 'Unknown Status'
    };

    return (
      <div key={order.id} className="order-card">
        <div className="order-card-header">
          <div className="order-details">
            <span className="order-id">Order #{order.id}</span>
            <span className="order-time">
              {new Date(order.createdAt).toLocaleString()}
            </span>
          </div>
          <div className="order-customer-info">
            <span className="customer-name">{order.customerName}</span>
            <span className="customer-phone">{order.customerPhone || 'No phone'}</span>
          </div>
        </div>

        <div className="order-items-list">
          {order.orderItems.map(item => (
            <div key={item.id} className="order-item">
              <div className="item-details">
                <span className="item-quantity">{item.quantity}x</span>
                <span className="item-name">{item.name}</span>
              </div>
              <span className="item-price">
                ${(item.price * item.quantity).toFixed(2)}
              </span>
            </div>
          ))}
        </div>

        <div className="order-total">
          <span>Total</span>
          <span>${order.total.toFixed(2)}</span>
        </div>

        {order.specialInstructions && (
          <div className="order-instructions">
            <strong>Special Instructions:</strong>
            <p>{order.specialInstructions}</p>
          </div>
        )}

        <div className="order-actions">
          {statusInfo.nextStatus && (
            <button 
              className={`order-action-btn order-action-btn-${statusInfo.color}`}
              onClick={() => handleUpdateOrderStatus(order.id, statusInfo.nextStatus)}
            >
              {statusInfo.nextLabel}
            </button>
          )}
          {order.status === 'NEW' && (
            <button 
              className="order-action-btn order-action-btn-danger"
              onClick={() => handleUpdateOrderStatus(order.id, 'CANCELLED')}
            >
              Reject Order
            </button>
          )}
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="order-processing-container">
        <div className="loading-spinner">Loading...</div>
      </div>
    );
  }

  return (
    <div className="order-processing-container">
      <div className="order-header">
        <h1>Order Processing</h1>
        <p>Manage and fulfill customer orders</p>
      </div>

      {restaurants.length === 0 ? (
        <div className="no-restaurants-message">
          <p>You don't have any restaurants yet. Create a restaurant first to manage orders.</p>
          <a href="/restaurant/create" className="create-restaurant-btn">Create Restaurant</a>
        </div>
      ) : (
        <>
          <div className="restaurant-selector">
            <div className="restaurant-selector-content">
              <label htmlFor="restaurant-select">Select Restaurant:</label>
              <select 
                id="restaurant-select" 
                value={selectedRestaurant?.id || ''} 
                onChange={handleRestaurantChange}
              >
                {restaurants.map(restaurant => (
                  <option key={restaurant.id} value={restaurant.id}>
                    {restaurant.name}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="orders-section">
            <div className="order-tabs">
              {Object.keys(ORDER_STATUS_FLOW).map(status => (
                <div 
                  key={status}
                  className={`order-tab ${activeTab === status ? 'active' : ''}`}
                  onClick={() => setActiveTab(status)}
                >
                  {status === 'NEW' ? 'New Orders' : 
                   status === 'PROCESSING' ? 'Preparing' : 
                   status === 'READY_FOR_PICKUP' ? 'Ready for Pickup' : 
                   'Out for Delivery'}
                </div>
              ))}
              <div 
                key="completed"
                className={`order-tab ${activeTab === 'completed' ? 'active' : ''}`}
                onClick={() => setActiveTab('completed')}
              >
                Completed
              </div>
            </div>

            <div className="orders-list">
              {orders.filter(order => 
                (activeTab === 'completed' 
                  ? order.status === 'COMPLETED' || order.status === 'CANCELLED'
                  : order.status === activeTab)
              ).length > 0 ? (
                orders
                  .filter(order => 
                    (activeTab === 'completed' 
                      ? order.status === 'COMPLETED' || order.status === 'CANCELLED'
                      : order.status === activeTab)
                  )
                  .map(renderOrderCard)
              ) : (
                <div className="no-orders">
                  <p>No orders in this category.</p>
                  <p>Orders will appear here when customers place them.</p>
                </div>
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default OrderProcessing;
