// src/pages/delivery/AvailableOrders.jsx
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import '../../styles/delivery/AvailableOrders.css';


const AvailableOrders = () => {
  const { currentUser } = useAuth();
  const navigate = useNavigate();
  const [availableOrders, setAvailableOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [refreshing, setRefreshing] = useState(false);

  // Use a more robust fetch function with better error handling and debugging
  const fetchAvailableOrders = useCallback(async (showRefreshing = false) => {
    try {
      if (showRefreshing) {
        setRefreshing(true);
      } else {
        setLoading(true);
      }

      const token = localStorage.getItem('token');

      console.log('Fetching available orders for delivery...');

      // Make sure we're using exactly the right endpoint expected by backend
      const response = await axios.get('/api/delivery/available-orders', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      console.log('Available orders response:', response.data);

      // Check if we got actual data
      if (response.data && Array.isArray(response.data)) {
        setAvailableOrders(response.data);
        setError(null);
      } else {
        console.warn('No orders data returned or unexpected data format', response.data);
        setAvailableOrders([]);
      }
    } catch (err) {
      console.error('Error fetching available orders:', err);
      console.error('Response data:', err.response?.data);
      console.error('Response status:', err.response?.status);

      setError('Failed to fetch available orders. Please try again later.');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  }, []);

  useEffect(() => {
    fetchAvailableOrders();

    // Reduce polling interval for better testing - can adjust for production
    const interval = setInterval(() => fetchAvailableOrders(true), 15000);

    return () => clearInterval(interval);
  }, [fetchAvailableOrders]);

  const handleAcceptOrder = async (orderId) => {
    try {
      setLoading(true);

      const token = localStorage.getItem('token');

      // Explicitly log what we're about to do
      console.log(`Accepting order ${orderId} for delivery`);

      const response = await axios.post(`/api/delivery/orders/${orderId}/accept`, {}, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      console.log('Order acceptance response:', response.data);

      // Navigate to the delivery order page
      navigate(`/delivery/order/${orderId}`);
    } catch (err) {
      console.error('Error accepting order:', err);
      console.error('Error details:', err.response?.data);

      setError('Failed to accept order. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Format distance for display
  const formatDistance = (distance) => {
    if (!distance) return 'Unknown';

    if (distance < 1) {
      return `${Math.round(distance * 1000)} m`;
    }
    return `${distance.toFixed(1)} km`;
  };

  // Manual refresh button handler
  const handleManualRefresh = () => {
    fetchAvailableOrders();
  };

  if (loading && !refreshing) return (
    <div className="container mx-auto px-4 py-8">
      <div className="text-center p-10">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto mb-4"></div>
        <p>Loading available orders...</p>
      </div>
    </div>
  );

  return (
    <div className="container mx-auto px-4 py-8">
      {refreshing && (
        <div className="fixed top-4 right-4 bg-blue-500 text-white px-3 py-1 rounded-full text-sm animate-pulse">
          Refreshing...
        </div>
      )}

      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Available Orders</h1>

        <button 
          onClick={handleManualRefresh} 
          className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition flex items-center"
          disabled={refreshing || loading}
        >
          <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
          </svg>
          Refresh
        </button>
      </div>

      {error && (
        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-6 rounded" role="alert">
          <p className="font-bold">Error</p>
          <p>{error}</p>
          <button 
            onClick={handleManualRefresh}
            className="mt-2 text-sm text-red-700 underline"
          >
            Try again
          </button>
        </div>
      )}

      {/* Debug info */}
      <div className="bg-blue-50 border-l-4 border-blue-500 p-4 mb-6 rounded">
        <p className="text-sm text-blue-700">
          <strong>Status:</strong> {refreshing ? 'Refreshing data...' : 'Ready'} | 
          <strong> Orders available:</strong> {availableOrders.length}
        </p>
        <p className="text-xs text-blue-600">Orders with READY_FOR_PICKUP status will appear here for delivery.</p>
      </div>

      {availableOrders.length === 0 ? (
        <div className="text-center p-10 bg-gray-50 rounded-lg">
          <p className="text-gray-600">No orders available for delivery at this time.</p>
          <p className="text-gray-500 mt-2">Orders marked "Ready for Pickup" by restaurants will appear here.</p>
          <button 
            onClick={handleManualRefresh} 
            className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
          >
            Refresh
          </button>
        </div>
      ) : (
        <div className="grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-3">
          {availableOrders.map((order) => (
            <div key={order.id} className="bg-white rounded-lg shadow-md p-6 border border-gray-200 hover:shadow-lg transition">
              <div className="flex justify-between items-start mb-4">
                <h2 className="text-lg font-semibold">Order #{order.id}</h2>
                <span className="px-2 py-1 rounded-full text-xs font-semibold bg-green-100 text-green-800">
                  Ready for pickup
                </span>
              </div>

              <div className="mb-4">
                <h3 className="font-medium mb-2">Restaurant:</h3>
                <p className="text-sm">{order.restaurant?.name || 'Unknown Restaurant'}</p>
                <p className="text-sm text-gray-500">{order.restaurant?.address}</p>
                <p className="text-sm text-gray-500 mt-1">
                  Distance: {formatDistance(order.restaurantDistance)}
                </p>
              </div>

              <div className="mb-4">
                <h3 className="font-medium mb-2">Delivery to:</h3>
                <p className="text-sm">{order.deliveryAddress?.street || order.deliveryAddress}</p>
                {order.deliveryAddress?.city && (
                  <p className="text-sm text-gray-500">
                    {order.deliveryAddress.city}, {order.deliveryAddress.zipCode}
                  </p>
                )}
                <p className="text-sm text-gray-500 mt-1">
                  Distance from restaurant: {formatDistance(order.deliveryDistance)}
                </p>
              </div>

              <div className="mb-4">
                <h3 className="font-medium mb-2">Order summary:</h3>
                <p className="text-sm">{order.orderItems?.length || order.itemCount || 0} items</p>
                <p className="text-sm font-medium mt-1">Estimated delivery time: {order.estimatedDeliveryTime || '30-45'} min</p>
              </div>

              <div className="flex justify-between items-center mb-4">
                <span className="font-medium">Delivery fee:</span>
                <span className="font-bold">${(order.deliveryFee || 0).toFixed(2)}</span>
              </div>

              <button
                onClick={() => handleAcceptOrder(order.id)}
                className="w-full px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
              >
                Accept Delivery
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AvailableOrders;
