// src/pages/restaurant/RestaurantDashboard.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import '../../styles/restaurant/RestaurantDashboard.css';

const RestaurantDashboard = () => {
  const { user } = useAuth();
  const [restaurantData, setRestaurantData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    ordersToday: 0,
    revenue: 0,
    menuItems: 0,
    averageRating: 0
  });

  useEffect(() => {
    // In a real app, this would fetch data from your API
    const fetchRestaurantData = async () => {
      try {
        setLoading(true);

        // Simulate API call with timeout
        await new Promise(resolve => setTimeout(resolve, 800));

        // Mock data for development purposes
        const mockRestaurant = {
          id: 1,
          name: "Your Restaurant",
          cuisine: "Mixed",
          address: "123 Main Street",
          createdAt: new Date().toISOString()
        };

        setRestaurantData(mockRestaurant);

        // Mock statistics
        setStats({
          ordersToday: Math.floor(Math.random() * 20),
          revenue: (Math.random() * 1000).toFixed(2),
          menuItems: Math.floor(Math.random() * 50),
          averageRating: (Math.random() * 2 + 3).toFixed(1) // Random rating between 3 and 5
        });

        setLoading(false);
      } catch (error) {
        console.error('Error fetching restaurant data:', error);
        setLoading(false);
      }
    };

    fetchRestaurantData();
  }, []);

  if (loading) {
    return (
      <div className="restaurant-dashboard-container">
        <div className="loading-spinner">Loading dashboard data...</div>
      </div>
    );
  }

  // Format dates
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  return (
    <div className="restaurant-dashboard-container">
      <div className="dashboard-header">
        <h1>Restaurant Dashboard</h1>
        <p>Welcome back, {user?.name || 'Restaurant Owner'}!</p>

        {restaurantData ? (
          <div className="restaurant-info">
            <h2>{restaurantData.name}</h2>
            <p>Cuisine: {restaurantData.cuisine} | Address: {restaurantData.address}</p>
            <p>Member since: {formatDate(restaurantData.createdAt)}</p>
          </div>
        ) : (
          <div className="create-restaurant-banner">
            <p>You haven't created a restaurant profile yet.</p>
            <Link to="/restaurant/create" className="create-restaurant-button">
              Create Restaurant Profile
            </Link>
          </div>
        )}
      </div>

      <div className="restaurant-stats-container">
        <div className="stat-card">
          <h3>Orders Today</h3>
          <p className="stat-value">{stats.ordersToday}</p>
        </div>
        <div className="stat-card">
          <h3>Total Revenue</h3>
          <p className="stat-value">${stats.revenue}</p>
        </div>
        <div className="stat-card">
          <h3>Menu Items</h3>
          <p className="stat-value">{stats.menuItems}</p>
        </div>
        <div className="stat-card">
          <h3>Average Rating</h3>
          <p className="stat-value">â˜… {stats.averageRating}</p>
        </div>
      </div>

      <div className="quick-actions">
        <h2>Quick Actions</h2>
        <div className="action-buttons">
          <Link to="/restaurant/menu" className="action-button">
            <i className="fas fa-utensils"></i>
            <span>Manage Menu</span>
          </Link>
          <Link to="/restaurant/orders" className="action-button">
            <i className="fas fa-clipboard-list"></i>
            <span>Process Orders</span>
          </Link>
          <Link to="/restaurant/settings" className="action-button">
            <i className="fas fa-cog"></i>
            <span>Settings</span>
          </Link>
        </div>
      </div>

      <div className="recent-orders-section">
        <div className="section-header">
          <h2>Recent Orders</h2>
          <Link to="/restaurant/orders" className="view-all-link">View All</Link>
        </div>

        <div className="no-orders-message">
          <p>No recent orders to display.</p>
          <small>New orders will appear here when customers place them.</small>
        </div>
      </div>

      <div className="performance-metrics">
        <h2>Performance Metrics</h2>
        <div className="metrics-cards">
          <div className="metric-card">
            <h3>Popular Items</h3>
            <div className="empty-metric">
              <p>Start adding menu items to see your most popular dishes.</p>
              <Link to="/restaurant/menu" className="action-link">Add Menu Items</Link>
            </div>
          </div>
          <div className="metric-card">
            <h3>Customer Feedback</h3>
            <div className="empty-metric">
              <p>Customer ratings and reviews will appear here.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RestaurantDashboard;
