// frontend/src/pages/delivery/DeliveryOrderPage.jsx
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../../styles/delivery/DeliveryOrderPage.css';

const DeliveryOrderPage = () => {
  const { orderId } = useParams();
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [tracking, setTracking] = useState({
    isPickedUp: false,
    isDelivered: false
  });

  useEffect(() => {
    const fetchOrderDetails = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`/api/delivery/orders/${orderId}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        setOrder(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching order details:', err);
        setError(err.response?.data?.message || 'Failed to fetch order details');
        setLoading(false);
      }
    };

    fetchOrderDetails();
  }, [orderId]);

  const updateOrderStatus = async (status) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `/api/delivery/orders/${orderId}/status`, 
        { status },
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );

      // Update local tracking state
      if (status === 'OUT_FOR_DELIVERY') {
        setTracking(prev => ({ ...prev, isPickedUp: true }));
      } else if (status === 'DELIVERED') {
        setTracking(prev => ({ ...prev, isDelivered: true }));

        // Redirect to dashboard after successful delivery
        setTimeout(() => navigate('/delivery/dashboard'), 2000);
      }
    } catch (err) {
      console.error('Error updating order status:', err);
      setError(err.response?.data?.message || 'Failed to update order status');
    }
  };

  if (loading) {
    return <div className="loading-container">Loading order details...</div>;
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{error}</p>
        <button onClick={() => navigate('/delivery/dashboard')}>
          Back to Dashboard
        </button>
      </div>
    );
  }

  return (
    <div className="order-delivery-container">
      <div className="order-delivery-header">
        <h1>Order Delivery</h1>
        <p>Order #{order.id}</p>
      </div>

      {/* Order Status Progress */}
      <div className="order-progress">
        {['Pickup', 'Out for Delivery', 'Delivered'].map((step, index) => (
          <div 
            key={step} 
            className={`progress-step ${
              (index === 0 && tracking.isPickedUp) ||
              (index === 1 && tracking.isDelivered) ? 'active' : ''
            }`}
          >
            <div className="step-number">{index + 1}</div>
            <div className="step-title">{step}</div>
          </div>
        ))}
      </div>

      {/* Order Details Sections */}
      {/* (You can add similar sections from the previous DeliveryOrderPage) */}

      {/* Action Buttons */}
      <div className="delivery-actions">
        {!tracking.isPickedUp && (
          <button 
            className="action-button primary"
            onClick={() => updateOrderStatus('OUT_FOR_DELIVERY')}
          >
            Pick Up Order
          </button>
        )}

        {tracking.isPickedUp && !tracking.isDelivered && (
          <button 
            className="action-button primary"
            onClick={() => updateOrderStatus('DELIVERED')}
          >
            Mark as Delivered
          </button>
        )}
      </div>
    </div>
  );
};

export default DeliveryOrderPage;
