// src/pages/restaurant/ManageMenu.jsx
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';
import '../../styles/restaurant/ManageMenu.css';

const ManageMenu = () => {
  const { user } = useAuth();
  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('all');

  // For adding new items
  const [showAddForm, setShowAddForm] = useState(false);
  const [newItem, setNewItem] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    imageUrl: '',
    popular: false
  });

  // For editing items
  const [editItem, setEditItem] = useState(null);
  const [showEditForm, setShowEditForm] = useState(false);

  useEffect(() => {
    const fetchMenuData = async () => {
      try {
        setIsLoading(true);
        // In a real app, this would be an API call to fetch the restaurant's menu
        // For demo purposes, we'll use mock data
        await new Promise(resolve => setTimeout(resolve, 800));

        const mockMenuData = [
          {
            id: 'm1',
            category: 'Pizzas',
            items: [
              {
                id: 'p1',
                name: 'Margherita',
                description: 'Classic pizza with tomato sauce, mozzarella, fresh basil, salt, and olive oil',
                price: 12.99,
                imageUrl: 'https://images.unsplash.com/photo-1574071318508-1cdbab80d002?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
                popular: true
              },
              {
                id: 'p2',
                name: 'Pepperoni',
                description: 'Classic pizza topped with tomato sauce, mozzarella, and pepperoni slices',
                price: 14.99,
                imageUrl: 'https://images.unsplash.com/photo-1628840042765-356cda07504e?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
                popular: true
              },
              {
                id: 'p3',
                name: 'Vegetarian',
                description: 'Fresh vegetables including bell peppers, mushrooms, onions, olives, and tomatoes',
                price: 13.99,
                imageUrl: 'https://images.unsplash.com/photo-1571407970349-bc81e7e96d47?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
                popular: false
              }
            ]
          },
          {
            id: 'm2',
            category: 'Sides',
            items: [
              {
                id: 's1',
                name: 'Garlic Bread',
                description: 'Crispy bread with garlic butter and herbs',
                price: 4.99,
                imageUrl: 'https://images.unsplash.com/photo-1619531038896-14930062c9b8?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
                popular: true
              },
              {
                id: 's2',
                name: 'Mozzarella Sticks',
                description: 'Breaded and deep-fried mozzarella cheese sticks served with marinara sauce',
                price: 6.99,
                imageUrl: 'https://images.unsplash.com/photo-1548340748-6d2b7d7da280?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
                popular: false
              }
            ]
          },
          {
            id: 'm3',
            category: 'Desserts',
            items: [
              {
                id: 'd1',
                name: 'Tiramisu',
                description: 'Classic Italian dessert made with layers of coffee-soaked ladyfingers and mascarpone cream',
                price: 7.99,
                imageUrl: 'https://images.unsplash.com/photo-1571877899820-49e1bdc8df4a?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
                popular: true
              },
              {
                id: 'd2',
                name: 'Chocolate Lava Cake',
                description: 'Warm chocolate cake with a molten chocolate center, served with vanilla ice cream',
                price: 8.99,
                imageUrl: 'https://images.unsplash.com/photo-1617305855058-336d24456869?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
                popular: false
              }
            ]
          },
          {
            id: 'm4',
            category: 'Drinks',
            items: [
              {
                id: 'dr1',
                name: 'Coca-Cola',
                description: 'Classic soft drink',
                price: 2.49,
                imageUrl: 'https://images.unsplash.com/photo-1554866585-cd94860890b7?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
                popular: false
              },
              {
                id: 'dr2',
                name: 'Sparkling Water',
                description: 'Refreshing carbonated water',
                price: 1.99,
                imageUrl: 'https://images.unsplash.com/photo-1578661138235-d91bc397b3c7?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
                popular: false
              }
            ]
          }
        ];

        setCategories(mockMenuData);
      } catch (error) {
        console.error("Failed to fetch menu data:", error);
        setError("Failed to load menu. Please try again later.");
      } finally {
        setIsLoading(false);
      }
    };

    fetchMenuData();
  }, []);

  const handleAddItem = () => {
    // In a real app, this would send a POST request to the API
    const categoryIndex = categories.findIndex(cat => cat.category === newItem.category);

    if (categoryIndex === -1) {
      // Create a new category
      const newCategory = {
        id: `m${categories.length + 1}`,
        category: newItem.category,
        items: [
          {
            id: `new-${Date.now()}`,
            ...newItem,
            price: parseFloat(newItem.price)
          }
        ]
      };

      setCategories([...categories, newCategory]);
    } else {
      // Add to existing category
      const updatedCategories = [...categories];
      updatedCategories[categoryIndex].items.push({
        id: `new-${Date.now()}`,
        ...newItem,
        price: parseFloat(newItem.price)
      });

      setCategories(updatedCategories);
    }

    // Reset form
    setNewItem({
      name: '',
      description: '',
      price: '',
      category: '',
      imageUrl: '',
      popular: false
    });

    setShowAddForm(false);
  };

  const handleEditItem = () => {
    if (!editItem) return;

    // Find category and item to update
    const categoryIndex = categories.findIndex(cat => 
      cat.items.some(item => item.id === editItem.id)
    );

    if (categoryIndex === -1) return;

    const itemIndex = categories[categoryIndex].items.findIndex(
      item => item.id === editItem.id
    );

    if (itemIndex === -1) return;

    // Create updated categories
    const updatedCategories = [...categories];
    updatedCategories[categoryIndex].items[itemIndex] = {
      ...editItem,
      price: parseFloat(editItem.price)
    };

    setCategories(updatedCategories);
    setShowEditForm(false);
    setEditItem(null);
  };

  const handleDeleteItem = (categoryId, itemId) => {
    // In a real app, this would send a DELETE request to the API
    if (window.confirm("Are you sure you want to delete this item?")) {
      const categoryIndex = categories.findIndex(cat => cat.id === categoryId);

      if (categoryIndex === -1) return;

      const updatedCategories = [...categories];
      updatedCategories[categoryIndex].items = updatedCategories[categoryIndex].items.filter(
        item => item.id !== itemId
      );

      // If category is now empty, consider removing it
      if (updatedCategories[categoryIndex].items.length === 0) {
        updatedCategories.splice(categoryIndex, 1);
      }

      setCategories(updatedCategories);
    }
  };

  const openEditForm = (item) => {
    setEditItem(item);
    setShowEditForm(true);
  };

  const allItems = categories.flatMap(category => 
    category.items.map(item => ({ ...item, categoryId: category.id, categoryName: category.category }))
  );

  const popularItems = allItems.filter(item => item.popular);

  const displayItems = activeTab === 'all' 
    ? allItems 
    : activeTab === 'popular' 
      ? popularItems 
      : allItems.filter(item => item.categoryName === activeTab);

  if (isLoading) {
    return (
      <div className="menu-manager-container">
        <div className="loading-spinner">Loading menu data...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="menu-manager-container">
        <div className="error-message">
          <h3>Error</h3>
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="menu-manager-container">
      <div className="menu-header">
        <h1>Menu Management</h1>
        <p>Manage your restaurant's menu items</p>
        <button className="add-item-button" onClick={() => setShowAddForm(true)}>
          <i className="fas fa-plus"></i> Add New Item
        </button>
      </div>

      <div className="menu-tabs">
        <button 
          className={`menu-tab ${activeTab === 'all' ? 'active' : ''}`}
          onClick={() => setActiveTab('all')}
        >
          All Items
        </button>
        <button 
          className={`menu-tab ${activeTab === 'popular' ? 'active' : ''}`}
          onClick={() => setActiveTab('popular')}
        >
          Popular
        </button>
        {categories.map(category => (
          <button 
            key={category.id}
            className={`menu-tab ${activeTab === category.category ? 'active' : ''}`}
            onClick={() => setActiveTab(category.category)}
          >
            {category.category}
          </button>
        ))}
      </div>

      <div className="menu-items-grid">
        {displayItems.length > 0 ? (
          displayItems.map(item => (
            <div key={item.id} className="menu-item-card">
              <div 
                className="menu-item-image" 
                style={{ backgroundImage: `url(${item.imageUrl || 'https://via.placeholder.com/150'})` }}
              >
                {item.popular && <span className="popular-badge">Popular</span>}
              </div>
              <div className="menu-item-details">
                <h3>{item.name}</h3>
                <p className="menu-item-category">{item.categoryName}</p>
                <p className="menu-item-description">{item.description}</p>
                <p className="menu-item-price">${item.price.toFixed(2)}</p>
              </div>
              <div className="menu-item-actions">
                <button 
                  className="edit-button"
                  onClick={() => openEditForm(item)}
                >
                  <i className="fas fa-edit"></i> Edit
                </button>
                <button 
                  className="delete-button"
                  onClick={() => handleDeleteItem(item.categoryId, item.id)}
                >
                  <i className="fas fa-trash"></i> Delete
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="no-items-message">
            <p>No menu items found in this category.</p>
          </div>
        )}
      </div>

      {/* Add New Item Modal */}
      {showAddForm && (
        <div className="modal-backdrop">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Add New Menu Item</h2>
              <button className="close-button" onClick={() => setShowAddForm(false)}>Ã—</button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label htmlFor="name">Item Name*</label>
                <input
                  type="text"
                  id="name"
                  value={newItem.name}
                  onChange={(e) => setNewItem({...newItem, name: e.target.value})}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="category">Category*</label>
                <input
                  type="text"
                  id="category"
                  value={newItem.category}
                  onChange={(e) => setNewItem({...newItem, category: e.target.value})}
                  list="existing-categories"
                  required
                />
                <datalist id="existing-categories">
                  {categories.map(cat => (
                    <option key={cat.id} value={cat.category} />
                  ))}
                </datalist>
                <small>Type an existing category or create a new one</small>
              </div>

              <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea
                  id="description"
                  value={newItem.description}
                  onChange={(e) => setNewItem({...newItem, description: e.target.value})}
                  rows="3"
                />
              </div>

              <div className="form-group">
                <label htmlFor="price">Price*</label>
                <input
                  type="number"
                  id="price"
                  value={newItem.price}
                  onChange={(e) => setNewItem({...newItem, price: e.target.value})}
                  step="0.01"
                  min="0"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="imageUrl">Image URL</label>
                <input
                  type="text"
                  id="imageUrl"
                  value={newItem.imageUrl}
                  onChange={(e) => setNewItem({...newItem, imageUrl: e.target.value})}
                  placeholder="https://example.com/image.jpg"
                />
              </div>

              <div className="form-group checkbox-group">
                <input
                  type="checkbox"
                  id="popular"
                  checked={newItem.popular}
                  onChange={(e) => setNewItem({...newItem, popular: e.target.checked})}
                />
                <label htmlFor="popular">Mark as Popular</label>
              </div>
            </div>
            <div className="modal-footer">
              <button className="cancel-button" onClick={() => setShowAddForm(false)}>Cancel</button>
              <button 
                className="save-button" 
                onClick={handleAddItem}
                disabled={!newItem.name || !newItem.category || !newItem.price}
              >
                Add Item
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Edit Item Modal */}
      {showEditForm && editItem && (
        <div className="modal-backdrop">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Edit Menu Item</h2>
              <button className="close-button" onClick={() => setShowEditForm(false)}>Ã—</button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label htmlFor="edit-name">Item Name*</label>
                <input
                  type="text"
                  id="edit-name"
                  value={editItem.name}
                  onChange={(e) => setEditItem({...editItem, name: e.target.value})}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="edit-category">Category*</label>
                <input
                  type="text"
                  id="edit-category"
                  value={editItem.categoryName}
                  onChange={(e) => setEditItem({...editItem, categoryName: e.target.value})}
                  list="existing-categories"
                  disabled // Disabling category change for simplicity
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="edit-description">Description</label>
                <textarea
                  id="edit-description"
                  value={editItem.description}
                  onChange={(e) => setEditItem({...editItem, description: e.target.value})}
                  rows="3"
                />
              </div>

              <div className="form-group">
                <label htmlFor="edit-price">Price*</label>
                <input
                  type="number"
                  id="edit-price"
                  value={editItem.price}
                  onChange={(e) => setEditItem({...editItem, price: e.target.value})}
                  step="0.01"
                  min="0"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="edit-imageUrl">Image URL</label>
                <input
                  type="text"
                  id="edit-imageUrl"
                  value={editItem.imageUrl}
                  onChange={(e) => setEditItem({...editItem, imageUrl: e.target.value})}
                />
              </div>

              <div className="form-group checkbox-group">
                <input
                  type="checkbox"
                  id="edit-popular"
                  checked={editItem.popular}
                  onChange={(e) => setEditItem({...editItem, popular: e.target.checked})}
                />
                <label htmlFor="edit-popular">Mark as Popular</label>
              </div>
            </div>
            <div className="modal-footer">
              <button className="cancel-button" onClick={() => setShowEditForm(false)}>Cancel</button>
              <button 
                className="save-button" 
                onClick={handleEditItem}
                disabled={!editItem.name || !editItem.price}
              >
                Save Changes
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageMenu;
