import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const AvailableOrders = () => {
  const { currentUser } = useAuth();
  const navigate = useNavigate();
  const [availableOrders, setAvailableOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [notification, setNotification] = useState(null);
  const wsRef = useRef(null);
  const retryQueueRef = useRef([]);

  // Initialize WebSocket connection for real-time updates
  useEffect(() => {
    if (!currentUser || !currentUser.token) return;

    // Close any existing connection
    if (wsRef.current) {
      wsRef.current.close();
    }

    // Create new WebSocket connection
    const wsUrl = `${process.env.REACT_APP_WS_URL || 'wss://api.yourbackend.com'}/orders`;
    wsRef.current = new WebSocket(wsUrl);

    wsRef.current.onopen = () => {
      console.log('WebSocket connection established');
      // Identify as a delivery person
      wsRef.current.send(JSON.stringify({
        type: 'IDENTIFY',
        role: 'DELIVERY',
        deliveryPersonId: currentUser.id
      }));

      // Retry any failed operations when connection is available
      if (retryQueueRef.current.length > 0) {
        retryFailedOperations();
      }
    };

    wsRef.current.onmessage = (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'NEW_AVAILABLE_ORDER') {
        // Handle new order available for delivery
        showNotification('New order available for delivery!');
        fetchAvailableOrders();
      } else if (data.type === 'ORDER_ASSIGNED') {
        // Handle order assigned to this delivery person
        if (data.deliveryPersonId === currentUser.id) {
          showNotification(`Order #${data.orderId} has been assigned to you.`);
          // Redirect to delivery order page
          navigate(`/delivery/order/${data.orderId}`);
        }
      } else if (data.type === 'ORDER_UPDATE') {
        // General order updates
        if (data.status === 'READY_FOR_PICKUP') {
          showNotification('New order ready for pickup!');
          fetchAvailableOrders();
        }
      }
    };

    wsRef.current.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    wsRef.current.onclose = () => {
      console.log('WebSocket connection closed');
    };

    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, [currentUser, navigate]);

  // Function to show notification
  const showNotification = (message) => {
    setNotification(message);
    // Clear notification after 5 seconds
    setTimeout(() => {
      setNotification(null);
    }, 5000);
  };

  // Store failed operations for retry
  const storeFailedOperation = (type, data) => {
    const newOperation = {
      id: Date.now(),
      type,
      data,
      timestamp: new Date()
    };

    retryQueueRef.current = [...retryQueueRef.current, newOperation];

    // Store in localStorage for persistence
    try {
      localStorage.setItem('deliveryFailedOperations', JSON.stringify(retryQueueRef.current));
    } catch (err) {
      console.error('Error storing failed operation:', err);
    }
  };

  // Remove operation from retry queue
  const removeFailedOperation = (operationId) => {
    retryQueueRef.current = retryQueueRef.current.filter(op => op.id !== operationId);

    // Update localStorage
    try {
      localStorage.setItem('deliveryFailedOperations', JSON.stringify(retryQueueRef.current));
    } catch (err) {
      console.error('Error updating failed operations in storage:', err);
    }
  };

  // Retry failed operations
  const retryFailedOperations = async () => {
    const operations = [...retryQueueRef.current];

    for (const op of operations) {
      try {
        if (op.type === 'acceptOrder') {
          await acceptOrderWithRetry(op.data.orderId, 1);
          removeFailedOperation(op.id);
        }
        // Add other operation types here
      } catch (err) {
        console.error('Failed to retry operation:', err);
        // Keep in queue for later retry
      }
    }
  };

  // Load any failed operations from localStorage on component mount
  useEffect(() => {
    try {
      const storedOperations = localStorage.getItem('deliveryFailedOperations');
      if (storedOperations) {
        retryQueueRef.current = JSON.parse(storedOperations);
      }
    } catch (err) {
      console.error('Error loading failed operations:', err);
    }
  }, []);

  // Enhanced function to fetch available orders with error handling
  const fetchAvailableOrders = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      const response = await axios.get('/api/delivery/available-orders', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      setAvailableOrders(response.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch available orders. Please try again later.');
      setLoading(false);
      console.error('Error fetching available orders:', err);
    }
  };

  // Set up initial data fetch and polling as fallback
  useEffect(() => {
    fetchAvailableOrders();

    // Set up polling as fallback to WebSockets
    // This ensures we still get updates if WebSockets fail
    const interval = setInterval(fetchAvailableOrders, 30000);

    return () => clearInterval(interval);
  }, [currentUser]);

  // Enhanced accept order with retry logic
  const acceptOrderWithRetry = async (orderId, maxRetries = 3) => {
    let retries = 0;

    while (retries < maxRetries) {
      try {
        console.log(`Accepting order ${orderId} (attempt ${retries + 1}/${maxRetries})`);

        const response = await axios.post(`/api/delivery/orders/${orderId}/accept`, {}, {
          headers: {
            Authorization: `Bearer ${currentUser.token}`
          }
        });

        // After successful acceptance, send confirmation via WebSocket
        if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
          wsRef.current.send(JSON.stringify({
            type: 'ORDER_ACCEPTED',
            orderId,
            deliveryPersonId: currentUser.id
          }));
        }

        // Generate a unique pickup code
        const pickupCode = Math.floor(1000 + Math.random() * 9000);

        // Store pickup code for verification at restaurant
        await axios.post(`/api/delivery/orders/${orderId}/pickup-code`, 
          { pickupCode },
          {
            headers: {
              Authorization: `Bearer ${currentUser.token}`
            }
          }
        );

        showNotification(`Order #${orderId} accepted successfully. Your pickup code is ${pickupCode}`);

        // Redirect to the delivery order page
        navigate(`/delivery/order/${orderId}`);

        return response;
      } catch (err) {
        retries++;
        console.error(`Error accepting order (attempt ${retries}/${maxRetries}):`, err);

        if (retries >= maxRetries) {
          // After all retries, store failed operation for later retry
          storeFailedOperation('acceptOrder', { orderId });
          setError(`Failed to accept order. It will be retried automatically when connection is restored.`);
          throw err;
        }

        // Wait before retrying (exponential backoff)
        await new Promise(resolve => setTimeout(resolve, 1000 * Math.pow(2, retries)));
      }
    }
  };

  const handleAcceptOrder = async (orderId) => {
    try {
      await acceptOrderWithRetry(orderId);
    } catch (err) {
      setError('Failed to accept order. Please try again.');
      console.error('Error accepting order:', err);
    }
  };

  // Format distance for display
  const formatDistance = (distance) => {
    if (distance < 1) {
      return `${Math.round(distance * 1000)} m`;
    }
    return `${distance.toFixed(1)} km`;
  };

  if (loading) return (
    <div className="text-center p-10">
      <div className="spinner-border" role="status">
        <span className="sr-only">Loading available orders...</span>
      </div>
      <p className="mt-2">Loading available orders...</p>
    </div>
  );

  if (error) return (
    <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 m-4" role="alert">
      <p>{error}</p>
      <button 
        onClick={() => fetchAvailableOrders()}
        className="mt-2 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
      >
        Try Again
      </button>
    </div>
  );

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Notification Banner */}
      {notification && (
        <div className="bg-blue-100 border-l-4 border-blue-500 text-blue-700 p-4 mb-4 flex justify-between items-center">
          <p>{notification}</p>
          <button onClick={() => setNotification(null)} className="text-blue-700">âœ•</button>
        </div>
      )}

      <h1 className="text-2xl font-bold mb-6">Available Orders</h1>

      {availableOrders.length === 0 ? (
        <div className="text-center p-10 bg-gray-50 rounded-lg">
          <p className="text-gray-600">No orders available for delivery at this time.</p>
          <p className="text-gray-500 mt-2">Check back soon or refresh the page.</p>
          <button 
            onClick={() => fetchAvailableOrders()} 
            className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Refresh
          </button>
        </div>
      ) : (
        <div className="grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-3">
          {availableOrders.map((order) => (
            <div key={order.id} className="bg-white rounded-lg shadow-md p-6 border border-gray-200">
              <div className="flex justify-between items-start mb-4">
                <h2 className="text-lg font-semibold">Order #{order.id}</h2>
                <span className="px-2 py-1 rounded-full text-xs font-semibold bg-green-100 text-green-800">
                  Ready for pickup
                </span>
              </div>

              <div className="mb-4">
                <h3 className="font-medium mb-2">Restaurant:</h3>
                <p className="text-sm">{order.restaurant.name}</p>
                <p className="text-sm text-gray-500">{order.restaurant.address}</p>
                <p className="text-sm text-gray-500 mt-1">
                  Distance: {formatDistance(order.restaurantDistance)}
                </p>
              </div>

              <div className="mb-4">
                <h3 className="font-medium mb-2">Delivery to:</h3>
                <p className="text-sm">{order.deliveryAddress.street}</p>
                <p className="text-sm text-gray-500">
                  {order.deliveryAddress.city}, {order.deliveryAddress.zipCode}
                </p>
                <p className="text-sm text-gray-500 mt-1">
                  Distance from restaurant: {formatDistance(order.deliveryDistance)}
                </p>
              </div>

              <div className="mb-4">
                <h3 className="font-medium mb-2">Order summary:</h3>
                <p className="text-sm">{order.itemCount} items</p>
                <p className="text-sm font-medium mt-1">Estimated delivery time: {order.estimatedDeliveryTime} min</p>
              </div>

              <div className="flex justify-between items-center mb-4">
                <span className="font-medium">Delivery fee:</span>
                <span className="font-bold">${order.deliveryFee.toFixed(2)}</span>
              </div>

              <button
                onClick={() => handleAcceptOrder(order.id)}
                className="w-full px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                Accept Delivery
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AvailableOrders;
