// Improved AvailableOrders.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { deliveryApi } from '../../services/api';
import OrderCard from '../../components/delivery/OrderCard';
import LoadingSpinner from '../../components/ui/LoadingSpinner';
import '../../styles/delivery/AvailableOrders.css';
import ErrorAlert from '../../components/ui/ErrorAlert';

const AvailableOrders = () => {
  const { currentUser } = useAuth();
  const navigate = useNavigate();
  const [availableOrders, setAvailableOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  // Add distance filter state
  const [maxDistance, setMaxDistance] = useState(10); // Default 10km
  // Add sorting state
  const [sortBy, setSortBy] = useState('distance'); // 'distance', 'fee', 'time'

  const fetchAvailableOrders = useCallback(async () => {
    try {
      setLoading(true);
      const response = await deliveryApi.getAvailableOrders();
      setAvailableOrders(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch available orders. Please try again later.');
      console.error('Error fetching available orders:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchAvailableOrders();

    // Set up polling to refresh the list every 30 seconds
    const interval = setInterval(fetchAvailableOrders, 30000);

    return () => clearInterval(interval);
  }, [fetchAvailableOrders]);

  const handleAcceptOrder = async (orderId) => {
    try {
      await deliveryApi.acceptOrder(orderId);
      // Navigate to the delivery order page
      navigate(`/delivery/order/${orderId}`);
    } catch (err) {
      setError('Failed to accept order. Please try again.');
      console.error('Error accepting order:', err);
    }
  };

  // Apply filters and sorting
  const filteredAndSortedOrders = availableOrders
    // Filter by max distance
    .filter(order => 
      (order.restaurantDistance + order.deliveryDistance) <= maxDistance
    )
    // Sort by the selected criteria
    .sort((a, b) => {
      switch(sortBy) {
        case 'distance':
          return (a.restaurantDistance + a.deliveryDistance) - (b.restaurantDistance + b.deliveryDistance);
        case 'fee':
          return b.deliveryFee - a.deliveryFee;
        case 'time':
          return a.estimatedDeliveryTime - b.estimatedDeliveryTime;
        default:
          return 0;
      }
    });

  if (loading) return <LoadingSpinner message="Loading available orders..." />;

  if (error) return <ErrorAlert message={error} onRetry={fetchAvailableOrders} />;

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
        <h1 className="text-2xl font-bold mb-4 md:mb-0">Available Orders</h1>

        {/* Filter and Sort Controls */}
        <div className="flex flex-col md:flex-row space-y-2 md:space-y-0 md:space-x-4">
          <div className="flex items-center">
            <label htmlFor="distance-filter" className="mr-2 text-sm font-medium">Max Distance:</label>
            <select
              id="distance-filter"
              value={maxDistance}
              onChange={(e) => setMaxDistance(Number(e.target.value))}
              className="rounded border border-gray-300 px-2 py-1"
            >
              <option value={5}>5 km</option>
              <option value={10}>10 km</option>
              <option value={15}>15 km</option>
              <option value={20}>20 km</option>
              <option value={100}>Any</option>
            </select>
          </div>

          <div className="flex items-center">
            <label htmlFor="sort-by" className="mr-2 text-sm font-medium">Sort By:</label>
            <select
              id="sort-by"
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="rounded border border-gray-300 px-2 py-1"
            >
              <option value="distance">Distance</option>
              <option value="fee">Highest Fee</option>
              <option value="time">Delivery Time</option>
            </select>
          </div>
        </div>
      </div>

      <div className="bg-blue-50 border-l-4 border-blue-500 p-4 mb-6 rounded">
        <div className="flex">
          <div className="flex-shrink-0">
            <svg className="h-5 w-5 text-blue-400" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
            </svg>
          </div>
          <div className="ml-3">
            <p className="text-sm text-blue-700">
              Showing {filteredAndSortedOrders.length} of {availableOrders.length} available orders.
              {filteredAndSortedOrders.length > 0 && sortBy === 'fee' && 
                ` Sorted by highest delivery fee.`}
            </p>
          </div>
        </div>
      </div>

      {filteredAndSortedOrders.length === 0 ? (
        <div className="text-center p-10 bg-gray-50 rounded-lg">
          <p className="text-gray-600">No orders available for delivery at this time.</p>
          <p className="text-gray-500 mt-2">Check back soon or refresh the page.</p>
          <button 
            onClick={fetchAvailableOrders} 
            className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
          >
            Refresh
          </button>
        </div>
      ) : (
        <div className="grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-3">
          {filteredAndSortedOrders.map((order) => (
            <OrderCard 
              key={order.id} 
              order={order} 
              onAccept={handleAcceptOrder} 
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default AvailableOrders;
