// src/pages/delivery/DeliveryOrderPage.jsx
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import '../../styles/delivery/DeliveryOrderPage.css';


const DeliveryOrderPage = () => {
  const { orderId } = useParams();
  const { currentUser } = useAuth();
  const navigate = useNavigate();

  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentStatus, setCurrentStatus] = useState('');
  const [isConfirmModalOpen, setIsConfirmModalOpen] = useState(false);
  const [elapsedTime, setElapsedTime] = useState(0);
  const [refreshing, setRefreshing] = useState(false);
  const [statusUpdateSuccess, setStatusUpdateSuccess] = useState('');

  // Improved fetchOrderDetails with better error handling
  const fetchOrderDetails = useCallback(async (showRefreshing = false) => {
    try {
      if (showRefreshing) {
        setRefreshing(true);
      } else {
        setLoading(true);
      }

      console.log(`Fetching order details for order ID: ${orderId}...`);

      const token = localStorage.getItem('token');

      // Make API call to get order details
      const response = await axios.get(`/api/delivery/orders/${orderId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      console.log("Order details response:", response.data);

      if (response.data) {
        setOrder(response.data);
        setCurrentStatus(response.data.status);

        // Calculate elapsed time since order was accepted
        if (response.data.acceptedAt) {
          const acceptedTime = new Date(response.data.acceptedAt).getTime();
          const currentTime = new Date().getTime();
          const elapsed = Math.floor((currentTime - acceptedTime) / 60000); // minutes
          setElapsedTime(elapsed);
        }

        setError(null);
      } else {
        console.warn("No order data returned or unexpected format");
        setError("Order data could not be loaded.");
      }
    } catch (err) {
      console.error('Error fetching order details:', err);
      console.error('Error details:', err.response?.data);
      setError('Failed to fetch order details. Please try again later.');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  }, [orderId]);

  useEffect(() => {
    if (orderId) {
      fetchOrderDetails();

      // Set up polling for status updates - more frequent for responsiveness
      const interval = setInterval(() => fetchOrderDetails(true), 15000);
      return () => clearInterval(interval);
    }
  }, [orderId, fetchOrderDetails]);

  // Timer for elapsed time
  useEffect(() => {
    if (currentStatus === 'READY_FOR_PICKUP' || currentStatus === 'PICKED_UP') {
      const timer = setInterval(() => {
        setElapsedTime(prev => prev + 1);
      }, 60000); // Update every minute

      return () => clearInterval(timer);
    }
  }, [currentStatus]);

  // Improved update order status function
  const updateOrderStatus = async (newStatus) => {
    try {
      console.log(`Updating order ${orderId} to status ${newStatus}`);
      setStatusUpdateSuccess('');

      const token = localStorage.getItem('token');

      // Making direct API call with better debugging
      const response = await axios.put(
        `/api/delivery/orders/${orderId}/status`, 
        { status: newStatus },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      console.log("Order status update response:", response.data);

      // Update local state
      setCurrentStatus(newStatus);
      setStatusUpdateSuccess(`Order status updated to ${newStatus.replace('_', ' ')}`);

      // Refresh order details
      await fetchOrderDetails(true);

      // Clear success message after delay
      setTimeout(() => {
        setStatusUpdateSuccess('');
      }, 3000);

      // If order is completed, redirect to available orders after a delay
      if (newStatus === 'DELIVERED') {
        setTimeout(() => {
          navigate('/delivery/dashboard');
        }, 3000);
      }
    } catch (err) {
      console.error('Error updating order status:', err);
      console.error('Error details:', err.response?.data);
      setError('Failed to update order status. Please try again.');
    }
  };

  const handlePickupConfirm = () => {
    updateOrderStatus('PICKED_UP');
  };

  const handleDeliveryConfirm = () => {
    setIsConfirmModalOpen(true);
  };

  const completeDelivery = () => {
    updateOrderStatus('DELIVERED');
    setIsConfirmModalOpen(false);
  };

  const cancelConfirmation = () => {
    setIsConfirmModalOpen(false);
  };

  const openGoogleMaps = (address) => {
    // Ensure address object is valid
    if (!address) {
      console.error("Invalid address object:", address);
      return;
    }

    // Format the address for Google Maps URL
    const addressString = address.street ? 
      `${address.street}, ${address.city || ''}, ${address.zipCode || ''}` : 
      (typeof address === 'string' ? address : 'Unknown address');

    const formattedAddress = encodeURIComponent(addressString);
    window.open(`https://www.google.com/maps/search/?api=1&query=${formattedAddress}`, '_blank');
  };

  const callCustomer = (phoneNumber) => {
    if (!phoneNumber) {
      setError("Customer phone number not available");
      return;
    }
    window.location.href = `tel:${phoneNumber}`;
  };

  // Format time for timer display
  const formatElapsedTime = (minutes) => {
    if (minutes < 60) {
      return `${minutes} min`;
    }
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  // Manual refresh handler
  const handleManualRefresh = () => {
    fetchOrderDetails(true);
  };

  if (loading && !refreshing) return (
    <div className="container mx-auto px-4 py-8">
      <div className="text-center p-10">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto mb-4"></div>
        <p>Loading order details...</p>
      </div>
    </div>
  );

  if (error) return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 m-4 rounded" role="alert">
        <p className="font-bold">Error</p>
        <p>{error}</p>
        <button 
          onClick={handleManualRefresh}
          className="mt-2 text-sm underline"
        >
          Try Again
        </button>
      </div>
    </div>
  );

  if (!order) return (
    <div className="container mx-auto px-4 py-8">
      <div className="text-center p-10 bg-gray-50 rounded-lg">
        <p className="text-gray-600">Order not found</p>
        <button 
          onClick={() => navigate('/delivery/dashboard')}
          className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
        >
          Return to Dashboard
        </button>
      </div>
    </div>
  );

  return (
    <div className="container mx-auto px-4 py-8">
      {refreshing && (
        <div className="fixed top-4 right-4 bg-blue-500 text-white px-3 py-1 rounded-full text-sm animate-pulse">
          Refreshing...
        </div>
      )}

      {statusUpdateSuccess && (
        <div className="fixed top-4 left-1/2 transform -translate-x-1/2 bg-green-500 text-white px-4 py-2 rounded-full text-sm animate-pulse">
          {statusUpdateSuccess}
        </div>
      )}

      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Order #{orderId}</h1>

        <button
          onClick={handleManualRefresh}
          className="px-3 py-1 flex items-center text-sm bg-blue-100 text-blue-700 rounded hover:bg-blue-200 transition"
          disabled={refreshing}
        >
          {refreshing ? (
            <>
              <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-blue-700" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Refreshing...
            </>
          ) : (
            <>
              <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
              </svg>
              Refresh
            </>
          )}
        </button>
      </div>

      {/* Status Info Box */}
      <div className="bg-blue-50 border-l-4 border-blue-500 p-4 mb-6 rounded">
        <div className="flex items-center">
          <svg className="w-5 h-5 text-blue-500 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
          <p className="text-sm text-blue-700">
            <strong>Status Update:</strong> Orders marked as "Ready for Pickup" by restaurants will appear in your available orders. You must update the status to continue the flow.
          </p>
        </div>
      </div>

      {/* Status Banner with improved UI */}
      <div className={`mb-6 p-4 rounded-lg shadow-sm ${
        currentStatus === 'READY_FOR_PICKUP' ? 'bg-blue-100 border border-blue-200' :
        currentStatus === 'PICKED_UP' ? 'bg-yellow-100 border border-yellow-200' :
        currentStatus === 'DELIVERED' ? 'bg-green-100 border border-green-200' :
        'bg-gray-100 border border-gray-200'
      }`}>
        <div className="flex items-center mb-2">
          <h2 className="font-semibold text-lg">
            Status: {currentStatus.replace(/_/g, ' ')}
          </h2>
          {elapsedTime > 0 && (
            <span className="ml-4 px-2 py-1 bg-white rounded-full text-xs font-medium">
              Time elapsed: {formatElapsedTime(elapsedTime)}
            </span>
          )}
        </div>

        <div className="w-full bg-gray-200 rounded-full h-2.5">
          <div className={`h-2.5 rounded-full ${
            currentStatus === 'READY_FOR_PICKUP' ? 'w-1/3 bg-blue-600' :
            currentStatus === 'PICKED_UP' ? 'w-2/3 bg-yellow-600' :
            currentStatus === 'DELIVERED' ? 'w-full bg-green-600' :
            'w-0'
          }`}></div>
        </div>

        <div className="flex justify-between mt-2 text-xs text-gray-500">
          <span>Accepted</span>
          <span>Picked Up</span>
          <span>Delivered</span>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
        {/* Restaurant Info */}
        <div className="bg-white rounded-lg shadow-md p-6 border border-gray-200 hover:shadow-lg transition">
          <h2 className="text-lg font-semibold mb-4 flex items-center">
            <svg className="w-5 h-5 mr-2 text-red-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
            </svg>
            Restaurant
          </h2>
          <div className="mb-4">
            <p className="font-medium">{order.restaurant?.name || 'Unknown Restaurant'}</p>
            <p className="text-gray-500">{order.restaurant?.address?.street}</p>
            <p className="text-gray-500">
              {order.restaurant?.address?.city}, {order.restaurant?.address?.zipCode}
            </p>
            <p className="text-gray-500">{order.restaurant?.phone}</p>
          </div>

          <div className="flex flex-wrap gap-2">
            <button
              onClick={() => openGoogleMaps(order.restaurant?.address)}
              className="flex items-center px-4 py-2 bg-gray-100 text-gray-800 rounded-lg hover:bg-gray-200 transition"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
              </svg>
              Navigate to Restaurant
            </button>

            {order.restaurant?.phone && (
              <button
                onClick={() => window.location.href = `tel:${order.restaurant.phone}`}
                className="flex items-center px-4 py-2 bg-blue-100 text-blue-800 rounded-lg hover:bg-blue-200 transition"
              >
                <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" />
                </svg>
                Call Restaurant
              </button>
            )}
          </div>
        </div>

        {/* Customer Info */}
        <div className="bg-white rounded-lg shadow-md p-6 border border-gray-200 hover:shadow-lg transition">
          <h2 className="text-lg font-semibold mb-4 flex items-center">
            <svg className="w-5 h-5 mr-2 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
            </svg>
            Customer
          </h2>
          <div className="mb-4">
            <p className="font-medium">{order.customer?.name || 'Customer'}</p>
            <p className="text-gray-500">{order.deliveryAddress?.street || order.deliveryAddress}</p>
            {order.deliveryAddress?.city && (
              <p className="text-gray-500">
                {order.deliveryAddress.city}, {order.deliveryAddress.zipCode}
              </p>
            )}
            {order.deliveryInstructions && (
              <div className="mt-2 p-2 bg-yellow-50 border border-yellow-100 rounded text-sm">
                <p className="font-medium">Delivery Instructions:</p>
                <p>{order.deliveryInstructions}</p>
              </div>
            )}
          </div>

          <div className="flex flex-wrap gap-2">
            {order.customer?.phone && (
              <button
                onClick={() => callCustomer(order.customer.phone)}
                className="flex items-center px-4 py-2 bg-green-100 text-green-800 rounded-lg hover:bg-green-200 transition"
              >
                <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" />
                </svg>
                Call Customer
              </button>
            )}

            <button
              onClick={() => openGoogleMaps(order.deliveryAddress)}
              className="flex items-center px-4 py-2 bg-gray-100 text-gray-800 rounded-lg hover:bg-gray-200 transition"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
              </svg>
              Navigate to Customer
            </button>
          </div>
        </div>
      </div>

      {/* Order Details */}
      <div className="bg-white rounded-lg shadow-md p-6 border border-gray-200 mb-6 hover:shadow-lg transition">
        <h2 className="text-lg font-semibold mb-4 flex items-center">
          <svg className="w-5 h-5 mr-2 text-yellow-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-3 7h3m-3 4h3m-6-4h.01M9 16h.01" />
          </svg>
          Order Items
        </h2>
        <ul className="divide-y divide-gray-200">
          {(order.items || order.orderItems || []).map((item) => (
            <li key={item.id} className="py-3 flex justify-between">
              <div>
                <p>{item.quantity}x {item.menuItem?.name || item.name || item.menuItemName}</p>
                {item.specialInstructions && (
                  <p className="text-sm text-gray-500">Note: {item.specialInstructions}</p>
                )}
              </div>
              <p className="font-medium">${((item.price || 0) * (item.quantity || 1)).toFixed(2)}</p>
            </li>
          ))}
        </ul>

        <div className="mt-4 pt-4 border-t border-gray-200">
          <div className="flex justify-between mb-2">
            <span>Subtotal</span>
            <span>${(order.subtotal || 0).toFixed(2)}</span>
          </div>
          <div className="flex justify-between mb-2">
            <span>Delivery Fee</span>
            <span>${(order.deliveryFee || 0).toFixed(2)}</span>
          </div>
          <div className="flex justify-between font-bold">
            <span>Total</span>
            <span>${(order.totalPrice || order.total || 0).toFixed(2)}</span>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="bg-white rounded-lg shadow-md p-6 border border-gray-200">
        {currentStatus === 'READY_FOR_PICKUP' && (
          <button
            onClick={handlePickupConfirm}
            className="w-full px-4 py-3 bg-blue-500 text-white font-medium rounded-lg hover:bg-blue-600 transition flex items-center justify-center"
          >
            <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
            </svg>
            Confirm Pickup from Restaurant
          </button>
        )}

        {currentStatus === 'PICKED_UP' && (
          <button
            onClick={handleDeliveryConfirm}
            className="w-full px-4 py-3 bg-green-500 text-white font-medium rounded-lg hover:bg-green-600 transition flex items-center justify-center"
          >
            <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
            </svg>
            Confirm Delivery to Customer
          </button>
        )}

        {currentStatus === 'DELIVERED' && (
          <div className="text-center">
            <div className="flex items-center justify-center text-green-600 font-medium mb-2">
              <svg className="w-6 h-6 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              Delivery completed successfully!
            </div>
            <p className="text-gray-500">You will be redirected to the dashboard shortly...</p>
          </div>
        )}
      </div>

      {/* Confirmation Modal */}
      {isConfirmModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
            <h3 className="text-xl font-bold mb-4">Confirm Delivery</h3>
            <p className="mb-6">Are you sure you want to mark this order as delivered?</p>
            <div className="flex justify-end space-x-4">
              <button
                onClick={cancelConfirmation}
                className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300 transition"
              >
                Cancel
              </button>
              <button
                onClick={completeDelivery}
                className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 transition"
              >
                Confirm Delivery
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DeliveryOrderPage;
