// src/pages/restaurant/OrderProcessing.jsx
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import restaurantApi from '../../api/restaurantApi';
import '../../styles/restaurant/OrderProcessing.css';

// Simplified DeliveryTracker component without WebSocket dependency
const DeliveryTracker = ({ order }) => {
  const [deliveryStatus, setDeliveryStatus] = useState(order.status);

  // Update local state when order prop changes
  useEffect(() => {
    setDeliveryStatus(order.status);
  }, [order.status]);

  // Define status text and colors
  const getStatusInfo = (status) => {
    switch (status) {
      case 'READY_FOR_PICKUP':
        return { text: 'Ready for pickup', color: 'blue' };
      case 'OUT_FOR_DELIVERY':
        return { text: 'Out for delivery', color: 'orange' };
      case 'DELIVERED':
        return { text: 'Delivered', color: 'green' };
      default:
        return { text: status.replace(/_/g, ' '), color: 'gray' };
    }
  };

  const statusInfo = getStatusInfo(deliveryStatus);

  return (
    <div className="delivery-tracker">
      <h4 className={`delivery-status delivery-status-${statusInfo.color}`}>
        Delivery Status: {statusInfo.text}
      </h4>

      <div className="delivery-progress">
        <div className="progress-bar">
          <div className="progress-step completed">
            <div className="step-marker"></div>
            <div className="step-label">Confirmed</div>
          </div>
          <div className={`progress-step ${['READY_FOR_PICKUP', 'OUT_FOR_DELIVERY', 'DELIVERED'].includes(deliveryStatus) ? 'completed' : ''}`}>
            <div className="step-marker"></div>
            <div className="step-label">Ready</div>
          </div>
          <div className={`progress-step ${['OUT_FOR_DELIVERY', 'DELIVERED'].includes(deliveryStatus) ? 'completed' : ''}`}>
            <div className="step-marker"></div>
            <div className="step-label">Picked Up</div>
          </div>
          <div className={`progress-step ${deliveryStatus === 'DELIVERED' ? 'completed' : ''}`}>
            <div className="step-marker"></div>
            <div className="step-label">Delivered</div>
          </div>
        </div>
      </div>
    </div>
  );
};

const OrderProcessing = () => {
  const { user } = useAuth();
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurantId, setSelectedRestaurantId] = useState(null);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('all'); // 'all', 'pending', 'confirmed', etc.
  const [notification, setNotification] = useState(null);
  const [pickupCodes, setPickupCodes] = useState({}); // Store pickup codes for each order

  // Function to show notification
  const showNotification = (message) => {
    setNotification(message);
    // Clear notification after 5 seconds
    setTimeout(() => {
      setNotification(null);
    }, 5000);
  };

  // Fetch owned restaurants and then orders
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        console.log("Fetching restaurants owned by user...");

        // Step 1: Get restaurants owned by the current user
        const restaurantsResponse = await restaurantApi.getMyRestaurants();

        if (!restaurantsResponse.success) {
          setError(restaurantsResponse.error || 'Failed to load your restaurants');
          setLoading(false);
          return;
        }

        console.log("Restaurants response:", restaurantsResponse.data);

        if (restaurantsResponse.data.length === 0) {
          setError('You don\'t have any restaurants. Please create a restaurant first.');
          setLoading(false);
          return;
        }

        setRestaurants(restaurantsResponse.data);

        // Step 2: Set the first restaurant as selected by default
        const firstRestaurantId = restaurantsResponse.data[0].id;
        setSelectedRestaurantId(firstRestaurantId);

        // Step 3: Fetch orders for the selected restaurant
        await fetchRestaurantOrders(firstRestaurantId);

      } catch (err) {
        console.error("Error in initial data fetch:", err);
        setError('An unexpected error occurred. Please try again later.');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Function to fetch orders for a specific restaurant
  const fetchRestaurantOrders = async (restaurantId, status = null) => {
    try {
      console.log(`Fetching orders for restaurant ID: ${restaurantId}, status: ${status || 'all'}`);

      const response = await restaurantApi.getRestaurantOrders(restaurantId, status);

      if (response.success) {
        console.log("Orders data:", response.data);
        setOrders(response.data);
      } else {
        console.error("Failed to fetch orders:", response.error);
        setError(response.error || 'Failed to fetch orders');
      }
    } catch (err) {
      console.error("Error fetching restaurant orders:", err);
      setError('Failed to fetch orders. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Handle restaurant change in dropdown
  const handleRestaurantChange = (e) => {
    const newRestaurantId = parseInt(e.target.value);
    setSelectedRestaurantId(newRestaurantId);
    fetchRestaurantOrders(newRestaurantId, activeTab === 'all' ? null : activeTab);
  };

  // Handle status tab change
  const handleTabChange = (tab) => {
    setActiveTab(tab);
    if (selectedRestaurantId) {
      fetchRestaurantOrders(selectedRestaurantId, tab === 'all' ? null : tab);
    }
  };

  // Update order status
  const updateOrderStatus = async (orderId, newStatus) => {
    try {
      console.log(`Updating order ${orderId} to status ${newStatus}`);

      // Add this to debug
      console.log("Before API call to update status");

      const response = await restaurantApi.updateOrderStatus(orderId, newStatus);

      // Add this to debug
      console.log("After API call to update status, response:", response);

      if (response.success) {
        console.log("Order status updated successfully:", response.data);
        showNotification(`Order #${orderId} status updated to ${newStatus}`);

        // Refresh orders after status update
        fetchRestaurantOrders(selectedRestaurantId, activeTab === 'all' ? null : activeTab);
      } else {
        console.error("Failed to update order status:", response.error);
        setError(response.error || 'Failed to update order status');
      }
    } catch (err) {
      console.error("Error updating order status:", err);
      console.error("Error details:", err.response ? err.response.data : 'No response data');
      setError('An error occurred while updating the order status. Check console for details.');
    }
  };

  // Handle accepting an order
  const handleAcceptOrder = async (orderId) => {
    await updateOrderStatus(orderId, 'CONFIRMED');
  };

  // Handle rejecting an order
  const handleRejectOrder = async (orderId) => {
    await updateOrderStatus(orderId, 'CANCELLED');
  };

  // Handle marking order as ready for pickup
  const handleMarkAsReady = async (orderId) => {
    await updateOrderStatus(orderId, 'READY_FOR_PICKUP');
  };

  // Handle pickup code input change
  const handlePickupCodeChange = (orderId, code) => {
    setPickupCodes({
      ...pickupCodes,
      [orderId]: code
    });
  };

  // Handle verify pickup code
  const handleVerifyPickupCode = async (orderId) => {
    const code = pickupCodes[orderId];
    if (!code) {
      setError("Please enter a pickup code");
      return;
    }

    try {
      // This method would need to be implemented in your restaurantApi
      const response = await restaurantApi.verifyPickupCode(orderId, code);

      if (response.success) {
        showNotification(`Pickup verified for Order #${orderId}`);
        await fetchRestaurantOrders(selectedRestaurantId, activeTab === 'all' ? null : activeTab);
      } else {
        setError('Invalid pickup code');
      }
    } catch (err) {
      console.error("Error verifying pickup code:", err);
      setError('Failed to verify pickup code');
    }
  };

  // Format date for display
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  if (loading) {
    return <div className="loading-container">Loading orders...</div>;
  }

  if (error) {
    return (
      <div className="restaurant-dashboard-container">
        <div className="error-message">
          <h3>Error</h3>
          <p>{error}</p>
          <button 
            className="btn btn-primary"
            onClick={() => window.location.reload()}
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="restaurant-dashboard-container">
      {notification && (
        <div className="notification-banner">
          <p>{notification}</p>
          <button onClick={() => setNotification(null)}>Ã—</button>
        </div>
      )}

      <div className="dashboard-header">
        <h1>Process Orders</h1>
        <p>Manage and update customer orders</p>
      </div>

      {/* Restaurant Selector */}
      {restaurants.length > 1 && (
        <div className="restaurant-selector">
          <div className="restaurant-selector-content">
            <label htmlFor="restaurant-select">Select Restaurant:</label>
            <select 
              id="restaurant-select"
              value={selectedRestaurantId || ''}
              onChange={handleRestaurantChange}
            >
              {restaurants.map(restaurant => (
                <option key={restaurant.id} value={restaurant.id}>
                  {restaurant.name}
                </option>
              ))}
            </select>
          </div>
        </div>
      )}

      <div className="order-status-tabs">
        <button 
          className={activeTab === 'all' ? 'active' : ''} 
          onClick={() => handleTabChange('all')}
        >
          All Orders
        </button>
        <button 
          className={activeTab === 'PENDING' ? 'active' : ''} 
          onClick={() => handleTabChange('PENDING')}
        >
          Pending
        </button>
        <button 
          className={activeTab === 'CONFIRMED' ? 'active' : ''} 
          onClick={() => handleTabChange('CONFIRMED')}
        >
          Confirmed
        </button>
        <button 
          className={activeTab === 'READY_FOR_PICKUP' ? 'active' : ''} 
          onClick={() => handleTabChange('READY_FOR_PICKUP')}
        >
          Ready for Pickup
        </button>
        <button 
          className={activeTab === 'OUT_FOR_DELIVERY' ? 'active' : ''} 
          onClick={() => handleTabChange('OUT_FOR_DELIVERY')}
        >
          Out for Delivery
        </button>
        <button 
          className={activeTab === 'DELIVERED' ? 'active' : ''} 
          onClick={() => handleTabChange('DELIVERED')}
        >
          Delivered
        </button>
      </div>

      <div className="orders-list">
        {orders.length === 0 ? (
          <p className="no-orders-message">No orders found with the selected status.</p>
        ) : (
          orders.map(order => (
            <div key={order.id} className="order-card">
              <div className="order-header">
                <div className="order-id-section">
                  <h3>Order #{order.id}</h3>
                  <span className={`order-status ${order.status.toLowerCase()}`}>
                    {order.status.replace(/_/g, ' ')}
                  </span>
                </div>
                <div className="order-time">
                  {formatDate(order.createdAt)}
                </div>
              </div>

              <div className="order-content">
                <div className="customer-details">
                  <h4>Customer Details</h4>
                  <p><strong>Name:</strong> {order.userName}</p>
                  <p><strong>Address:</strong> {order.deliveryAddress}</p>
                  {order.specialInstructions && (
                    <p><strong>Instructions:</strong> {order.specialInstructions}</p>
                  )}
                </div>

                <div className="order-items-details">
                  <h4>Order Items</h4>
                  <ul className="order-items-list">
                    {order.orderItems.map((item) => (
                      <li key={item.id}>
                        <span className="item-name">{item.quantity}x {item.menuItemName}</span>
                        <span className="item-price">${(parseFloat(item.price) * item.quantity).toFixed(2)}</span>
                      </li>
                    ))}
                  </ul>
                  <div className="order-total">
                    <span>Total</span>
                    <span>${parseFloat(order.total).toFixed(2)}</span>
                  </div>
                </div>
              </div>

              {/* Add delivery tracker for orders in appropriate status */}
              {['READY_FOR_PICKUP', 'OUT_FOR_DELIVERY', 'DELIVERED'].includes(order.status) && (
                <DeliveryTracker order={order} />
              )}

              {/* Pickup code verification for ready orders */}
              {order.status === 'READY_FOR_PICKUP' && (
                <div className="pickup-verification">
                  <div className="pickup-code">
                    <input 
                      type="text" 
                      placeholder="Enter pickup code"
                      value={pickupCodes[order.id] || ''}
                      onChange={(e) => handlePickupCodeChange(order.id, e.target.value)}
                    />
                    <button 
                      className="verify-button"
                      onClick={() => handleVerifyPickupCode(order.id)}
                    >
                      Verify Pickup
                    </button>
                  </div>
                </div>
              )}

              <div className="order-actions">
                {order.status === 'PENDING' && (
                  <>
                    <button 
                      className="action-button accept"
                      onClick={() => handleAcceptOrder(order.id)}
                    >
                      Accept Order
                    </button>

                    <button 
                      className="action-button cancel"
                      onClick={() => handleRejectOrder(order.id)}
                    >
                      Cancel Order
                    </button>
                  </>
                )}

                {order.status === 'CONFIRMED' && (
                  <button 
                    className="action-button ready"
                    onClick={() => handleMarkAsReady(order.id)}
                  >
                    Mark as Ready
                  </button>
                )}

                <button className="action-button print" onClick={() => window.print()}>Print Order</button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default OrderProcessing;
