// src/pages/restaurant/OrderProcessing.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import restaurantApi from '../../api/restaurantApi';
import '../../styles/restaurant/OrderProcessing.css';

const OrderProcessing = () => {
  const { user } = useAuth();
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurantId, setSelectedRestaurantId] = useState(null);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filterStatus, setFilterStatus] = useState('ALL');
  const [refreshing, setRefreshing] = useState(false);
  const [statusUpdateSuccess, setStatusUpdateSuccess] = useState('');

  // Define order status transitions mapping
  const ORDER_STATUS_FLOW = {
    'PENDING': 'CONFIRMED',
    'CONFIRMED': 'READY_FOR_PICKUP',
    'READY_FOR_PICKUP': 'OUT_FOR_DELIVERY', // This transition is handled by delivery personnel
    'OUT_FOR_DELIVERY': 'DELIVERED'         // This transition is handled by delivery personnel
  };

  // Order status display names for UI
  const ORDER_STATUS_DISPLAY = {
    'PENDING': 'Pending',
    'CONFIRMED': 'Confirmed',
    'READY_FOR_PICKUP': 'Ready for Pickup',
    'OUT_FOR_DELIVERY': 'Out for Delivery',
    'DELIVERED': 'Delivered',
    'CANCELLED': 'Cancelled'
  };

  // Fetch owned restaurants and then orders
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        console.log("Fetching restaurants owned by user...");

        // Get token from localStorage
        const token = localStorage.getItem('token');

        if (!token) {
          setError('Authentication required. Please log in again.');
          setLoading(false);
          return;
        }

        // Step 1: Get restaurants owned by the current user
        const response = await axios.get('/api/restaurants/owner', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        console.log("Restaurants response:", response.data);

        if (!response.data || response.data.length === 0) {
          setError('You don\'t have any restaurants. Please create a restaurant first.');
          setLoading(false);
          return;
        }

        setRestaurants(response.data);

        // Step 2: Set the first restaurant as selected by default
        const firstRestaurantId = response.data[0].id;
        setSelectedRestaurantId(firstRestaurantId);

        // Step 3: Fetch orders for the selected restaurant
        await fetchRestaurantOrders(firstRestaurantId);

      } catch (err) {
        console.error("Error in initial data fetch:", err);
        console.error("Error details:", err.response?.data);
        setError('An unexpected error occurred. Please try again later.');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Function to fetch orders for a specific restaurant
  const fetchRestaurantOrders = useCallback(async (restaurantId, status = null, showRefreshing = false) => {
    try {
      if (showRefreshing) {
        setRefreshing(true);
      } else if (!refreshing) {
        setLoading(true);
      }

      console.log(`Fetching orders for restaurant ID: ${restaurantId}, status: ${status || 'all'}`);

      const token = localStorage.getItem('token');

      // Build request URL with proper query parameters
      let url = `/api/restaurants/${restaurantId}/orders`;
      if (status && status !== 'ALL') {
        url += `?status=${status}`;
      }

      const response = await axios.get(url, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      console.log("Orders data received:", response.data);

      if (Array.isArray(response.data)) {
        setOrders(response.data);
      } else {
        console.warn("Unexpected response format:", response.data);
        setOrders([]);
      }
    } catch (err) {
      console.error("Error fetching restaurant orders:", err);
      console.error("Error response:", err.response?.data);
      setError('Failed to fetch orders. Please try again later.');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  }, [refreshing]);

  // Handle restaurant change in dropdown
  const handleRestaurantChange = (e) => {
    const newRestaurantId = parseInt(e.target.value);
    setSelectedRestaurantId(newRestaurantId);
    fetchRestaurantOrders(newRestaurantId, filterStatus === 'ALL' ? null : filterStatus);
  };

  // Handle manual refresh button click
  const handleManualRefresh = () => {
    if (selectedRestaurantId) {
      fetchRestaurantOrders(selectedRestaurantId, filterStatus === 'ALL' ? null : filterStatus, true);
    }
  };

  // Update order status function with debugging logs
  const updateOrderStatus = async (orderId, newStatus) => {
    try {
      console.log(`Updating order ${orderId} to status ${newStatus}`);
      setStatusUpdateSuccess('');

      const token = localStorage.getItem('token');

      // More explicit API call with better debugging
      console.log("Making API call to update order status...");

      const response = await axios.put(
        `/api/orders/${orderId}/status`,
        { status: newStatus },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      console.log("Order status update response:", response.data);

      // Show success message
      setStatusUpdateSuccess(`Order #${orderId} successfully marked as ${ORDER_STATUS_DISPLAY[newStatus]}`);

      // Refresh orders after status update
      await fetchRestaurantOrders(selectedRestaurantId, filterStatus === 'ALL' ? null : filterStatus, true);

      // Clear success message after 5 seconds
      setTimeout(() => {
        setStatusUpdateSuccess('');
      }, 5000);

    } catch (err) {
      console.error("Error updating order status:", err);
      console.error("Error details:", err.response ? err.response.data : 'No response data');
      setError('An error occurred while updating the order status. Check console for details.');
    }
  };

  // Format date for display
  const formatDate = (dateString) => {
    try {
      return new Date(dateString).toLocaleString();
    } catch (e) {
      return dateString;
    }
  };

  if (loading && !refreshing) {
    return (
      <div className="restaurant-dashboard-container">
        <div className="loading-container flex items-center justify-center h-64">
          <div className="loading-spinner">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
            <p className="mt-4 text-gray-600">Loading orders...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="restaurant-dashboard-container">
        <div className="error-message bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded">
          <h3 className="font-bold">Error</h3>
          <p>{error}</p>
          <button 
            className="mt-2 text-sm underline text-red-700"
            onClick={() => window.location.reload()}
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  // Filter orders based on selected status
  const filteredOrders = filterStatus === 'ALL' 
    ? orders 
    : orders.filter(order => order.status === filterStatus);

  return (
    <div className="restaurant-dashboard-container">
      <div className="restaurant-sidebar">
        <div className="restaurant-profile">
          <h3>Restaurant Dashboard</h3>
          <p>Welcome, {user?.name || 'Restaurant Manager'}</p>
        </div>
        <nav className="restaurant-nav">
          <Link to="/restaurant/dashboard" className="nav-item">Dashboard</Link>
          <Link to="/restaurant/menu" className="nav-item">Manage Menu</Link>
          <Link to="/restaurant/orders" className="nav-item active">Process Orders</Link>
          <Link to="/restaurant/settings" className="nav-item">Restaurant Profile</Link>
        </nav>
      </div>

      <div className="restaurant-main-content">
        <div className="dashboard-header flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold">Process Orders</h1>
            <p className="text-gray-600">Manage and update customer orders</p>
          </div>

          <button
            onClick={handleManualRefresh}
            className="px-4 py-2 flex items-center bg-blue-500 text-white rounded hover:bg-blue-600 transition"
            disabled={refreshing}
          >
            {refreshing ? (
              <>
                <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Refreshing...
              </>
            ) : (
              <>
                <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                </svg>
                Refresh Orders
              </>
            )}
          </button>
        </div>

        {statusUpdateSuccess && (
          <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4 mb-6 rounded animate-pulse">
            <p>{statusUpdateSuccess}</p>
          </div>
        )}

        {/* Restaurant Selector */}
        {restaurants.length > 1 && (
          <div className="restaurant-selector bg-white p-4 rounded-lg shadow mb-6">
            <div className="restaurant-selector-content">
              <label htmlFor="restaurant-select" className="block text-sm font-medium text-gray-700 mb-2">Select Restaurant:</label>
              <select 
                id="restaurant-select"
                value={selectedRestaurantId || ''}
                onChange={handleRestaurantChange}
                className="mt-1 block w-full sm:w-auto pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm rounded-md"
              >
                {restaurants.map(restaurant => (
                  <option key={restaurant.id} value={restaurant.id}>
                    {restaurant.name}
                  </option>
                ))}
              </select>
            </div>
          </div>
        )}

        {/* Status info box */}
        <div className="bg-blue-50 border-l-4 border-blue-500 text-blue-700 p-4 mb-6 rounded">
          <h3 className="font-medium">Order Status Flow</h3>
          <p className="text-sm">
            1. Pending → 2. Confirmed → 3. Ready for Pickup → 4. Out for Delivery → 5. Delivered
          </p>
          <p className="text-xs mt-1">
            Restaurant staff can update orders from "Pending" to "Ready for Pickup". Delivery personnel will see orders marked as "Ready for Pickup".
          </p>
        </div>

        <div className="order-status-tabs flex overflow-x-auto space-x-2 mb-6">
          <button 
            className={`px-4 py-2 rounded-lg transition ${filterStatus === 'ALL' ? 'bg-blue-600 text-white' : 'bg-gray-200 hover:bg-gray-300'}`}
            onClick={() => {
              setFilterStatus('ALL');
              fetchRestaurantOrders(selectedRestaurantId);
            }}
          >
            All Orders
          </button>
          <button 
            className={`px-4 py-2 rounded-lg transition ${filterStatus === 'PENDING' ? 'bg-blue-600 text-white' : 'bg-gray-200 hover:bg-gray-300'}`}
            onClick={() => {
              setFilterStatus('PENDING');
              fetchRestaurantOrders(selectedRestaurantId, 'PENDING');
            }}
          >
            Pending
          </button>
          <button 
            className={`px-4 py-2 rounded-lg transition ${filterStatus === 'CONFIRMED' ? 'bg-blue-600 text-white' : 'bg-gray-200 hover:bg-gray-300'}`}
            onClick={() => {
              setFilterStatus('CONFIRMED');
              fetchRestaurantOrders(selectedRestaurantId, 'CONFIRMED');
            }}
          >
            Confirmed
          </button>
          <button 
            className={`px-4 py-2 rounded-lg transition ${filterStatus === 'READY_FOR_PICKUP' ? 'bg-blue-600 text-white' : 'bg-gray-200 hover:bg-gray-300'}`}
            onClick={() => {
              setFilterStatus('READY_FOR_PICKUP');
              fetchRestaurantOrders(selectedRestaurantId, 'READY_FOR_PICKUP');
            }}
          >
            Ready for Pickup
          </button>
          <button 
            className={`px-4 py-2 rounded-lg transition ${filterStatus === 'OUT_FOR_DELIVERY' ? 'bg-blue-600 text-white' : 'bg-gray-200 hover:bg-gray-300'}`}
            onClick={() => {
              setFilterStatus('OUT_FOR_DELIVERY');
              fetchRestaurantOrders(selectedRestaurantId, 'OUT_FOR_DELIVERY');
            }}
          >
            Out for Delivery
          </button>
          <button 
            className={`px-4 py-2 rounded-lg transition ${filterStatus === 'DELIVERED' ? 'bg-blue-600 text-white' : 'bg-gray-200 hover:bg-gray-300'}`}
            onClick={() => {
              setFilterStatus('DELIVERED');
              fetchRestaurantOrders(selectedRestaurantId, 'DELIVERED');
            }}
          >
            Delivered
          </button>
        </div>

        <div className="orders-list">
          {filteredOrders.length === 0 ? (
            <div className="bg-white rounded-lg shadow p-8 text-center">
              <p className="text-gray-500 mb-4">No orders found with the selected status.</p>
              <button 
                onClick={handleManualRefresh}
                className="text-blue-500 underline"
              >
                Refresh to check for new orders
              </button>
            </div>
          ) : (
            filteredOrders.map(order => (
              <div key={order.id} className="order-card bg-white rounded-lg shadow-md p-6 border border-gray-200 mb-6 hover:shadow-lg transition">
                <div className="order-header flex flex-col md:flex-row md:justify-between md:items-center mb-4">
                  <div className="order-id-section mb-2 md:mb-0">
                    <h3 className="text-lg font-semibold">Order #{order.id}</h3>
                    <span className={`inline-block mt-1 px-3 py-1 rounded-full text-xs font-medium ${
                      order.status === 'PENDING' ? 'bg-yellow-100 text-yellow-800' :
                      order.status === 'CONFIRMED' ? 'bg-blue-100 text-blue-800' :
                      order.status === 'READY_FOR_PICKUP' ? 'bg-green-100 text-green-800' :
                      order.status === 'OUT_FOR_DELIVERY' ? 'bg-purple-100 text-purple-800' :
                      order.status === 'DELIVERED' ? 'bg-green-100 text-green-800' :
                      'bg-red-100 text-red-800'
                    }`}>
                      {ORDER_STATUS_DISPLAY[order.status] || order.status}
                    </span>
                  </div>
                  <div className="order-time text-sm text-gray-500">
                    {formatDate(order.createdAt)}
                  </div>
                </div>

                <div className="order-content grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="customer-details">
                    <h4 className="text-sm font-medium text-gray-500 uppercase tracking-wider mb-2">Customer Details</h4>
                    <p><strong>Name:</strong> {order.userName}</p>
                    <p><strong>Address:</strong> {order.deliveryAddress}</p>
                    {order.specialInstructions && (
                      <p className="mt-2 p-2 bg-yellow-50 border border-yellow-100 rounded text-sm">
                        <strong>Instructions:</strong> {order.specialInstructions}
                      </p>
                    )}
                  </div>

                  <div className="order-items-details">
                    <h4 className="text-sm font-medium text-gray-500 uppercase tracking-wider mb-2">Order Items</h4>
                    <ul className="order-items-list divide-y divide-gray-200">
                      {order.orderItems.map((item) => (
                        <li key={item.id} className="py-2 flex justify-between">
                          <span className="item-name">{item.quantity}x {item.menuItemName}</span>
                          <span className="item-price font-medium">${(parseFloat(item.price) * item.quantity).toFixed(2)}</span>
                        </li>
                      ))}
                    </ul>
                    <div className="order-total mt-2 pt-2 border-t border-gray-200 flex justify-between font-bold">
                      <span>Total</span>
                      <span>${parseFloat(order.total).toFixed(2)}</span>
                    </div>
                  </div>
                </div>

                <div className="order-actions flex flex-wrap gap-2 mt-6 pt-4 border-t border-gray-200">
                  {order.status === 'PENDING' && (
                    <button 
                      className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
                      onClick={() => updateOrderStatus(order.id, 'CONFIRMED')}
                    >
                      Accept Order
                    </button>
                  )}

                  {order.status === 'CONFIRMED' && (
                    <button 
                      className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 transition"
                      onClick={() => updateOrderStatus(order.id, 'READY_FOR_PICKUP')}
                    >
                      Mark as Ready for Pickup
                    </button>
                  )}

                  {(order.status === 'PENDING' || order.status === 'CONFIRMED') && (
                    <button 
                      className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 transition"
                      onClick={() => updateOrderStatus(order.id, 'CANCELLED')}
                    >
                      Cancel Order
                    </button>
                  )}

                  <button 
                    className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300 transition"
                    onClick={() => window.print()}
                  >
                    Print Order
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default OrderProcessing;
