// src/pages/delivery/DeliveryOrderHistory.jsx
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import '../../styles/delivery/DeliveryOrderPage.css';

const DeliveryOrderHistory = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeFilter, setActiveFilter] = useState('recent'); // 'recent', 'today', 'week', 'completed'

  // Order status mapping for display
  const ORDER_STATUS_MAP = {
    READY_FOR_PICKUP: { 
      label: 'Ready for Pickup', 
      color: 'blue',
      icon: 'ü•°'
    },
    OUT_FOR_DELIVERY: { 
      label: 'Out for Delivery', 
      color: 'orange',
      icon: 'üöö'
    },
    DELIVERED: { 
      label: 'Delivered', 
      color: 'green',
      icon: '‚úì'
    }
  };

  // Fetch delivery order history
  const fetchOrders = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      // Determine endpoint based on filter
      let endpoint = '/api/delivery/my-orders';

      if (activeFilter === 'today') {
        endpoint += '?timeframe=today';
      } else if (activeFilter === 'week') {
        endpoint += '?timeframe=week';
      } else if (activeFilter === 'completed') {
        endpoint += '?status=DELIVERED';
      }

      // Use full URL for reliability
      const response = await axios.get(`http://localhost:8080${endpoint}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      console.log('Delivery order history:', response.data);
      setOrders(response.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching delivery orders:', err);
      console.error('Error details:', err.response?.data);
      setError('Failed to load your delivery history. Please try again.');
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, [activeFilter]);

  // Calculate earnings from orders
  const calculateEarnings = (orders) => {
    // Filter only delivered orders
    const deliveredOrders = orders.filter(order => order.status === 'DELIVERED');
    // Sum the delivery fees
    return deliveredOrders.reduce((sum, order) => sum + (order.deliveryFee || 0), 0);
  };

  // Format date for display
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  // Get time ago for display
  const getTimeAgo = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const seconds = Math.floor((now - date) / 1000);

    if (seconds < 60) return `${seconds} seconds ago`;

    const minutes = Math.floor(seconds / 60);
    if (minutes < 60) return `${minutes} minute${minutes > 1 ? 's' : ''} ago`;

    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `${hours} hour${hours > 1 ? 's' : ''} ago`;

    const days = Math.floor(hours / 24);
    if (days < 7) return `${days} day${days > 1 ? 's' : ''} ago`;

    return formatDate(dateString).split(',')[0]; // Just the date part
  };

  if (loading) {
    return (
      <div className="delivery-history-container">
        <div className="loading-message">Loading your delivery history...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="delivery-history-container">
        <div className="error-message">
          <p>{error}</p>
          <button onClick={fetchOrders} className="retry-button">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="delivery-history-container">
      <div className="delivery-history-header">
        <h1>Delivery History</h1>
        <p>Your completed and in-progress deliveries</p>
      </div>

      {/* Earnings Summary */}
      <div className="earnings-summary">
        <div className="earnings-card">
          <h3>Today's Earnings</h3>
          <p className="earnings-amount">${calculateEarnings(
            orders.filter(order => {
              const today = new Date();
              const orderDate = new Date(order.createdAt);
              return orderDate.toDateString() === today.toDateString();
            })
          ).toFixed(2)}</p>
        </div>

        <div className="earnings-card">
          <h3>This Week's Earnings</h3>
          <p className="earnings-amount">${calculateEarnings(
            orders.filter(order => {
              const today = new Date();
              const weekAgo = new Date();
              weekAgo.setDate(today.getDate() - 7);
              const orderDate = new Date(order.createdAt);
              return orderDate >= weekAgo;
            })
          ).toFixed(2)}</p>
        </div>

        <div className="earnings-card">
          <h3>Completed Deliveries</h3>
          <p className="delivery-count">{
            orders.filter(order => order.status === 'DELIVERED').length
          }</p>
        </div>
      </div>

      {/* Filter Tabs */}
      <div className="delivery-filter-tabs">
        <button 
          className={activeFilter === 'recent' ? 'active' : ''}
          onClick={() => setActiveFilter('recent')}
        >
          Recent Orders
        </button>
        <button 
          className={activeFilter === 'today' ? 'active' : ''}
          onClick={() => setActiveFilter('today')}
        >
          Today's Orders
        </button>
        <button 
          className={activeFilter === 'week' ? 'active' : ''}
          onClick={() => setActiveFilter('week')}
        >
          This Week
        </button>
        <button 
          className={activeFilter === 'completed' ? 'active' : ''}
          onClick={() => setActiveFilter('completed')}
        >
          Completed
        </button>
      </div>

      {/* Orders List */}
      {orders.length === 0 ? (
        <div className="no-orders-message">
          <p>No delivery orders found for this filter.</p>
          <button 
            className="available-orders-btn"
            onClick={() => navigate('/delivery/available-orders')}
          >
            Find Available Orders
          </button>
        </div>
      ) : (
        <div className="orders-list">
          {orders.map(order => {
            const statusInfo = ORDER_STATUS_MAP[order.status] || {
              label: order.status.replace(/_/g, ' '),
              color: 'gray',
              icon: '‚ùì'
            };

            return (
              <div key={order.id} className="order-card">
                <div className="order-header">
                  <div className="order-info">
                    <h3>Order #{order.id}</h3>
                    <div className={`order-status status-${statusInfo.color}`}>
                      {statusInfo.icon} {statusInfo.label}
                    </div>
                  </div>
                  <div className="order-time">
                    {getTimeAgo(order.createdAt)}
                  </div>
                </div>

                <div className="order-details">
                  <div className="location-details">
                    <div className="pickup-location">
                      <h4>Pickup</h4>
                      <p>{order.restaurantName}</p>
                      <p className="address">{order.restaurant?.address || "Address not available"}</p>
                    </div>

                    <div className="delivery-location">
                      <h4>Delivery</h4>
                      <p>{order.userName}</p>
                      <p className="address">{order.deliveryAddress}</p>
                    </div>
                  </div>

                  <div className="order-summary">
                    <div className="order-items-count">
                      {order.orderItems?.length || 0} items
                    </div>
                    <div className="delivery-fee">
                      Delivery Fee: ${order.deliveryFee?.toFixed(2) || "0.00"}
                    </div>
                    <div className="order-total">
                      Total: ${order.total?.toFixed(2) || "0.00"}
                    </div>
                  </div>
                </div>

                <div className="order-actions">
                  {order.status === 'READY_FOR_PICKUP' && (
                    <button 
                      className="continue-delivery-btn"
                      onClick={() => navigate(`/delivery/order/${order.id}`)}
                    >
                      Continue Delivery
                    </button>
                  )}

                  {order.status === 'OUT_FOR_DELIVERY' && (
                    <button 
                      className="update-delivery-btn"
                      onClick={() => navigate(`/delivery/order/${order.id}`)}
                    >
                      Update Delivery
                    </button>
                  )}

                  {order.status === 'DELIVERED' && (
                    <button 
                      className="view-details-btn"
                      onClick={() => navigate(`/delivery/order/${order.id}`)}
                    >
                      View Details
                    </button>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      )}

      {/* Bottom Actions */}
      <div className="delivery-actions">
        <button 
          className="available-orders-btn primary"
          onClick={() => navigate('/delivery/available-orders')}
        >
          Find Available Orders
        </button>
        <button 
          className="refresh-btn"
          onClick={fetchOrders}
        >
          Refresh Orders
        </button>
      </div>
    </div>
  );
};

export default DeliveryOrderPage;
