// src/pages/delivery/DeliveryOrderPage.jsx
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import '../../styles/delivery/DeliveryOrderPage.css';

const DeliveryOrderPage = () => {
  const { orderId } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();

  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentStatus, setCurrentStatus] = useState('');
  const [isConfirmModalOpen, setIsConfirmModalOpen] = useState(false);

  useEffect(() => {
    const fetchOrderDetails = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem('token');

        if (!token) {
          setError('You need to be logged in to view this page');
          setLoading(false);
          return;
        }

        // Use the correct endpoint format
        const response = await axios.get(`http://localhost:8080/api/orders/${orderId}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (response.data) {
          setOrder(response.data);
          setCurrentStatus(response.data.status);
        } else {
          setError('Failed to fetch order details');
        }
      } catch (err) {
        console.error('Error fetching order details:', err);
        setError('Failed to fetch order details. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    if (orderId) {
      fetchOrderDetails();
    }
  }, [orderId]);

  const updateOrderStatus = async (newStatus) => {
    try {
      const token = localStorage.getItem('token');

      if (!token) {
        setError('You need to be logged in to perform this action');
        return;
      }

      await axios.put(`http://localhost:8080/api/orders/${orderId}/status`, 
        { status: newStatus },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setCurrentStatus(newStatus);

      // If order is completed, redirect to available orders after a delay
      if (newStatus === 'DELIVERED') {
        setTimeout(() => {
          navigate('/delivery/available-orders');
        }, 3000);
      }
    } catch (err) {
      console.error('Error updating order status:', err);
      setError('Failed to update order status. Please try again.');
    }
  };

  const handlePickupConfirm = () => {
    updateOrderStatus('OUT_FOR_DELIVERY');
  };

  const handleDeliveryConfirm = () => {
    setIsConfirmModalOpen(true);
  };

  const completeDelivery = () => {
    updateOrderStatus('DELIVERED');
    setIsConfirmModalOpen(false);
  };

  const cancelConfirmation = () => {
    setIsConfirmModalOpen(false);
  };

  if (loading) {
    return (
      <div className="delivery-order-container">
        <div className="loading-message">Loading order details...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="delivery-order-container">
        <div className="error-message">
          <h3>Error</h3>
          <p>{error}</p>
          <Link to="/delivery/available-orders" className="back-button">
            Back to Available Orders
          </Link>
        </div>
      </div>
    );
  }

  if (!order) {
    return (
      <div className="delivery-order-container">
        <div className="not-found-message">
          <h3>Order Not Found</h3>
          <p>The order you are looking for does not exist or has been deleted.</p>
          <Link to="/delivery/available-orders" className="back-button">
            Back to Available Orders
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="delivery-order-container">
      <div className="order-header">
        <h1>Order #{order.id}</h1>
        <div className={`status-badge ${currentStatus.toLowerCase()}`}>
          {currentStatus.replace('_', ' ')}
        </div>
      </div>

      <div className="order-progress">
        <div className={`progress-step ${currentStatus === 'READY_FOR_PICKUP' || currentStatus === 'OUT_FOR_DELIVERY' || currentStatus === 'DELIVERED' ? 'active' : ''}`}>
          <div className="step-icon">📦</div>
          <span className="step-label">Ready for Pickup</span>
        </div>
        <div className={`progress-step ${currentStatus === 'OUT_FOR_DELIVERY' || currentStatus === 'DELIVERED' ? 'active' : ''}`}>
          <div className="step-icon">🚚</div>
          <span className="step-label">Out for Delivery</span>
        </div>
        <div className={`progress-step ${currentStatus === 'DELIVERED' ? 'active' : ''}`}>
          <div className="step-icon">✅</div>
          <span className="step-label">Delivered</span>
        </div>
      </div>

      <div className="order-sections">
        <div className="info-section">
          <h2>Restaurant Information</h2>
          <div className="info-content">
            <p><strong>Name:</strong> {order.restaurantName}</p>
            <p><strong>Pickup Address:</strong> {order.restaurantAddress || 'Restaurant Address'}</p>
          </div>
        </div>

        <div className="info-section">
          <h2>Customer Information</h2>
          <div className="info-content">
            <p><strong>Name:</strong> {order.userName || 'Customer'}</p>
            <p><strong>Delivery Address:</strong> {order.deliveryAddress}</p>
            {order.specialInstructions && (
              <p><strong>Special Instructions:</strong> {order.specialInstructions}</p>
            )}
          </div>
        </div>

        <div className="info-section">
          <h2>Order Items</h2>
          <div className="items-list">
            {order.orderItems.map(item => (
              <div key={item.id} className="order-item">
                <div className="item-info">
                  <span className="item-quantity">{item.quantity}x</span>
                  <span className="item-name">{item.menuItemName}</span>
                </div>
                <span className="item-price">${(item.price).toFixed(2)}</span>
              </div>
            ))}
          </div>
          <div className="order-total">
            <div className="total-row">
              <span>Subtotal</span>
              <span>${order.subtotal.toFixed(2)}</span>
            </div>
            <div className="total-row">
              <span>Delivery Fee</span>
              <span>${order.deliveryFee.toFixed(2)}</span>
            </div>
            <div className="total-row">
              <span>Tax</span>
              <span>${order.tax.toFixed(2)}</span>
            </div>
            <div className="total-row grand-total">
              <span>Total</span>
              <span>${order.total.toFixed(2)}</span>
            </div>
          </div>
        </div>
      </div>

      <div className="action-buttons">
        {currentStatus === 'READY_FOR_PICKUP' && (
          <button 
            className="action-button primary"
            onClick={handlePickupConfirm}
          >
            Confirm Pickup
          </button>
        )}

        {currentStatus === 'OUT_FOR_DELIVERY' && (
          <button 
            className="action-button primary"
            onClick={handleDeliveryConfirm}
          >
            Mark as Delivered
          </button>
        )}

        {currentStatus === 'DELIVERED' && (
          <div className="delivery-complete-message">
            <p>✅ Delivery completed successfully!</p>
            <p>You will be redirected to available orders shortly...</p>
          </div>
        )}

        <Link to="/delivery/available-orders" className="action-button secondary">
          Back to All Orders
        </Link>
      </div>

      {/* Delivery Confirmation Modal */}
      {isConfirmModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Confirm Delivery</h3>
            <p>Are you sure you want to mark this order as delivered?</p>
            <div className="modal-actions">
              <button 
                className="modal-button secondary"
                onClick={cancelConfirmation}
              >
                Cancel
              </button>
              <button 
                className="modal-button primary"
                onClick={completeDelivery}
              >
                Confirm Delivery
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DeliveryOrderPage;
