// frontend/src/pages/delivery/DeliveryDashboard.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const DeliveryDashboard = () => {
  const { user } = useAuth();
  const [dashboardData, setDashboardData] = useState({
    activeDelivery: null,
    deliveredToday: 0,
    totalEarningsToday: 0,
    isActive: false,
    availableOrders: 0,
    recentDeliveries: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem('token');

        if (!token) {
          throw new Error('No authentication token found');
        }

        const response = await axios.get('http://localhost:8080/api/delivery/dashboard', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        console.log('Dashboard Response:', response.data);

        if (response.data) {
          setDashboardData({
            activeDelivery: response.data.activeDelivery || null,
            deliveredToday: response.data.deliveredToday || 0,
            totalEarningsToday: response.data.totalEarningsToday || 0,
            isActive: response.data.active || false,
            availableOrders: response.data.availableOrders || 0,
            recentDeliveries: response.data.recentDeliveries || []
          });
        } else {
          throw new Error('Invalid dashboard data');
        }

        setLoading(false);
      } catch (err) {
        console.error('Full error details:', err);

        const errorMessage = err.response?.data?.message || 
                             err.message || 
                             'Failed to fetch dashboard data. Please try again later.';

        setError(errorMessage);
        setLoading(false);
      }
    };

    fetchDashboardData();

    const interval = setInterval(fetchDashboardData, 60000);
    return () => clearInterval(interval);
  }, []); 

  const toggleActiveStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      const newStatus = !dashboardData.isActive;

      await axios.post('http://localhost:8080/api/delivery/toggle-status', 
        { isActive: newStatus },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );

      setDashboardData(prev => ({
        ...prev,
        isActive: newStatus
      }));
    } catch (err) {
      setError('Failed to update your status. Please try again.');
      console.error('Error toggling status:', err);
    }
  };

  if (loading) return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      <div className="text-center">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-blue-500 mx-auto mb-4"></div>
        <p className="text-xl text-gray-600">Loading dashboard...</p>
      </div>
    </div>
  );

  if (error) return (
    <div className="min-h-screen bg-red-50 flex items-center justify-center p-4">
      <div className="bg-white p-8 rounded-lg shadow-md text-center max-w-md w-full">
        <h2 className="text-2xl font-bold text-red-600 mb-4">Error</h2>
        <p className="text-gray-700 mb-6">{error}</p>
        <button 
          onClick={() => window.location.reload()} 
          className="px-6 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition"
        >
          Retry
        </button>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="flex flex-col md:flex-row justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-4 md:mb-0">Delivery Dashboard</h1>
          <div className="flex items-center space-x-4">
            <span className="text-sm font-medium text-gray-600">
              Status: {dashboardData.isActive ? 'Active' : 'Inactive'}
            </span>
            <button
              onClick={toggleActiveStatus}
              className={`px-4 py-2 rounded-lg text-white font-semibold transition ${
                dashboardData.isActive 
                  ? 'bg-red-500 hover:bg-red-600' 
                  : 'bg-green-500 hover:bg-green-600'
              }`}
            >
              {dashboardData.isActive ? 'Go Offline' : 'Go Online'}
            </button>
          </div>
        </div>

        {/* Active Delivery Section */}
        <div className="grid md:grid-cols-3 gap-6 mb-8">
          {/* Active Delivery Card */}
          <div className="md:col-span-2 bg-white rounded-lg shadow-md p-6">
            {dashboardData.activeDelivery ? (
              <div className="flex justify-between items-center">
                <div>
                  <h2 className="text-xl font-semibold mb-2">Active Delivery</h2>
                  <p className="text-gray-600">Order #{dashboardData.activeDelivery.id}</p>
                  <p className="text-sm text-gray-500">
                    Restaurant: {dashboardData.activeDelivery.restaurantName}
                  </p>
                </div>
                <Link
                  to={`/delivery/order/${dashboardData.activeDelivery.id}`}
                  className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition"
                >
                  View Details
                </Link>
              </div>
            ) : (
              <div className="text-center">
                <h2 className="text-xl font-semibold mb-2">No Active Delivery</h2>
                <p className="text-gray-600 mb-4">You're online and ready to accept deliveries.</p>
                {dashboardData.availableOrders > 0 && (
                  <Link
                    to="/delivery/available-orders"
                    className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition"
                  >
                    View Available Orders ({dashboardData.availableOrders})
                  </Link>
                )}
              </div>
            )}
          </div>

          {/* Quick Stats */}
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-white rounded-lg shadow-md p-4 text-center">
              <div className="text-3xl font-bold text-blue-600">{dashboardData.deliveredToday}</div>
              <div className="text-sm text-gray-500">Deliveries Today</div>
            </div>
            <div className="bg-white rounded-lg shadow-md p-4 text-center">
              <div className="text-3xl font-bold text-green-600">
                ${dashboardData.totalEarningsToday.toFixed(2)}
              </div>
              <div className="text-sm text-gray-500">Earnings</div>
            </div>
            <div className="bg-white rounded-lg shadow-md p-4 text-center col-span-2">
              <div className="text-3xl font-bold text-purple-600">{dashboardData.availableOrders}</div>
              <div className="text-sm text-gray-500">Available Orders</div>
              {dashboardData.availableOrders > 0 && (
                <Link 
                  to="/delivery/available-orders" 
                  className="text-blue-500 hover:underline text-sm mt-2 block"
                >
                  View Orders
                </Link>
              )}
            </div>
          </div>
        </div>

        {/* Recent Deliveries */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-semibold">Recent Deliveries</h2>
            <Link 
              to="/delivery/history" 
              className="text-blue-500 hover:underline text-sm"
            >
              View All
            </Link>
          </div>

          {dashboardData.recentDeliveries.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b">
                    <th className="py-2 text-left">Order ID</th>
                    <th className="py-2 text-left">Restaurant</th>
                    <th className="py-2 text-left">Date</th>
                    <th className="py-2 text-right">Earnings</th>
                  </tr>
                </thead>
                <tbody>
                  {dashboardData.recentDeliveries.map((delivery) => (
                    <tr key={delivery.orderId} className="border-b last:border-b-0">
                      <td className="py-2">#{delivery.orderId}</td>
                      <td className="py-2">{delivery.restaurantName}</td>
                      <td className="py-2">{new Date(delivery.completedAt).toLocaleDateString()}</td>
                      <td className="py-2 text-right">${delivery.earnings.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center text-gray-500 py-4">
              No recent deliveries
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DeliveryDashboard;
