// src/api/orderApi.js
import axios from 'axios';

const API_URL = 'http://localhost:8080/api/orders';

// Helper function to handle API errors
const handleApiError = (error, context) => {
  console.error(`OrderAPI Error (${context}):`, {
    message: error.message,
    status: error.response?.status,
    data: error.response?.data,
  });

  return { 
    success: false, 
    error: error.response?.data?.message || `Failed to ${context}. Please try again.`
  };
};

const orderApi = {
  // Create a new order
  createOrder: async (orderData) => {
    try {
      const token = localStorage.getItem('token');

      // Prepare complete order data
      const completeOrderData = {
        restaurantId: orderData.restaurantInfo.id,
        orderItems: orderData.map(item => ({
          menuItemId: item.id,
          quantity: item.quantity,
          price: item.price
        })),
        total: orderData.reduce((sum, item) => sum + (item.price * item.quantity), 0)
      };

      const response = await axios.post(API_URL, completeOrderData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      return { 
        success: true, 
        data: response.data 
      };
    } catch (error) {
      return handleApiError(error, 'create order');
    }
  },

  // Get user's orders (with optional status filter)
  getUserOrders: async (status = null) => {
    try {
      const token = localStorage.getItem('token');

      const config = {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        params: status ? { status } : {}
      };

      const response = await axios.get(API_URL, config);

      return { 
        success: true, 
        data: response.data 
      };
    } catch (error) {
      return handleApiError(error, 'fetch orders');
    }
  },

  // New method to get restaurant's orders
  getRestaurantOrders: async (restaurantId, status = null) => {
    try {
      const token = localStorage.getItem('token');

      const config = {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        params: {
          restaurantId,
          ...(status ? { status } : {})
        }
      };

      const response = await axios.get(`${API_URL}/restaurant`, config);

      return { 
        success: true, 
        data: response.data 
      };
    } catch (error) {
      return handleApiError(error, 'fetch restaurant orders');
    }
  },

  // Get a specific order by ID
  getOrderById: async (orderId) => {
    try {
      const token = localStorage.getItem('token');

      const response = await axios.get(`${API_URL}/${orderId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      return { 
        success: true, 
        data: response.data 
      };
    } catch (error) {
      return handleApiError(error, 'fetch order details');
    }
  },

  // Update order status (for both restaurant and customer)
  updateOrderStatus: async (orderId, newStatus) => {
    try {
      const token = localStorage.getItem('token');

      const response = await axios.put(`${API_URL}/${orderId}/status`, 
        { status: newStatus },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );

      return { 
        success: true, 
        data: response.data 
      };
    } catch (error) {
      return handleApiError(error, 'update order status');
    }
  },

  // Cancel an order
  cancelOrder: async (orderId) => {
    try {
      const token = localStorage.getItem('token');

      const response = await axios.put(`${API_URL}/${orderId}/cancel`, null, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      return { 
        success: true, 
        data: response.data 
      };
    } catch (error) {
      return handleApiError(error, 'cancel order');
    }
  },

  // Add a method to add special instructions to an existing order
  addSpecialInstructions: async (orderId, instructions) => {
    try {
      const token = localStorage.getItem('token');

      const response = await axios.put(`${API_URL}/${orderId}/instructions`, 
        { specialInstructions: instructions },
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );

      return { 
        success: true, 
        data: response.data 
      };
    } catch (error) {
      return handleApiError(error, 'add special instructions');
    }
  }
};

export default orderApi;
